INSERT INTO `creature_text` VALUES ('20787', '0', '0', 'You\'ve saved me, fleshling! Ameer will hear of this noble act.', '12', '0', '100', '0', '0', '0', 'Captain Tyralius');
INSERT INTO `creature_text` VALUES ('20904', '0', '0', 'I knew the prince would be angry but, I... I have not been myself. I had to let them out! The great one speaks to me, you see. Wait--outsiders. Kael\'thas did not send you! Good... I\'ll just tell the prince you released the prisoners!', '14', '0', '100', '0', '0', '11222', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20904', '1', '0', 'The naaru kept some of the most dangerous beings in existence here in these cells. Let me introduce you to another...', '14', '0', '100', '0', '0', '11223', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20904', '2', '0', 'Yes, yes... another! Your will is mine!', '14', '0', '100', '0', '0', '11224', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20904', '3', '0', 'Behold another terrifying creature of incomprehensible power!', '14', '0', '100', '0', '0', '11225', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20904', '4', '0', 'What is this? A lowly gnome? I will do better, O great one.', '14', '0', '100', '0', '0', '11226', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20904', '5', '0', 'Anarchy! Bedlam! Oh, you are so wise! Yes, I see it now, of course!', '14', '0', '100', '0', '0', '11227', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20904', '6', '0', 'One final cell remains. Yes, O great one, right away!', '14', '0', '100', '0', '0', '11228', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20904', '7', '0', 'Welcome, O great one. I am your humble servant.', '14', '0', '100', '0', '0', '11229', 'Warden Mellichar');
INSERT INTO `creature_text` VALUES ('20977', '0', '0', 'Where in Bonzo\'s Brass Buttons am I? And who are-- yaaghh, that\'s one mother of a headache!', '12', '0', '100', '0', '0', '11171', 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES ('20977', '1', '0', '\"Lowly\"? I don\'t care who you are, friend: no one refers to the mighty Millhouse Manastorm as \"lowly\"!', '12', '0', '100', '0', '0', '11172', 'Millhouse Manastorm');
