INSERT INTO `creature_text` VALUES ('24035', '0', '12', 'The vrykul are evil and must be destroyed', '12', '0', '100', '0', '0', '0', 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES ('24035', '0', '13', 'They\'re performing unholy rites down in the catacombs, and I\'m about to be the sacrifice', '12', '0', '100', '0', '0', '0', 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES ('24035', '0', '14', 'This is the end.', '12', '0', '100', '0', '0', '0', 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES ('24035', '0', '15', 'You have to put a stop to this madness before it\'s too late', '12', '0', '100', '0', '0', '0', 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES ('24035', '0', '16', 'You saved my life', '12', '0', '100', '0', '0', '0', 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES ('24052', '0', '0', 'We don\'t shoe no mules here. \'Sides, it\'s strictly expedition business right now.', '12', '7', '100', '1', '0', '0', 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES ('24052', '1', '0', 'Not my problem! If yer so antsy to get yerself a house built, do it \'ere in the fort and quit yer whinin\'!', '12', '7', '100', '274', '0', '0', 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES ('24052', '2', '0', 'Now, if you\'d be so kind as to get your ass away from my forge...', '12', '7', '100', '25', '0', '0', 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES ('24061', '0', '0', 'Guards!', '14', '7', '100', '0', '0', '0', 'James Ormsby');
INSERT INTO `creature_text` VALUES ('24155', '0', '0', 'Guards!', '14', '1', '100', '0', '0', '0', 'Tobias Sarkhoff');
INSERT INTO `creature_text` VALUES ('24178', '0', '0', '%s wakes up from the smell of fresh meat!', '16', '0', '100', '15', '0', '0', 'Shatterhorn on aggro line and roar at same time');
INSERT INTO `creature_text` VALUES ('24211', '0', '0', 'Blessings of the spirits of the land and air upon you.', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24211', '0', '1', 'I must return. Good hunting to you.', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24211', '0', '2', 'I owe you a life debt, stranger.', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24211', '0', '3', 'Strange ghosts walk the land. Be careful!', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24211', '0', '4', 'Thank you, stranger.', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24211', '0', '5', 'They took us one by one. I\'m sure there are other survivors.', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24211', '0', '6', 'We should never have traveled through the Rivenwood. Everyone knows this!', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24211', '0', '7', 'You saved me from certain death. I owe you.', '12', '0', '100', '1', '0', '0', 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES ('24238', '2', '0', 'Drive the invaders out!  For Queen Angerboda!', '14', '0', '100', '0', '0', '0', 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES ('24238', '1', '0', 'No one speaks of my mother in that manner!', '14', '0', '100', '0', '0', '0', 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES ('24321', '0', '0', '%s motions for silence.', '16', '0', '100', '397', '0', '0', 'King Ymiron emote');
INSERT INTO `creature_text` VALUES ('24321', '1', '0', 'Vrykul, your king implores you to listen!', '14', '0', '100', '22', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24321', '2', '0', 'The crowd gasps in horror.', '16', '0', '100', '53', '0', '0', 'King Ymiron emote');
INSERT INTO `creature_text` VALUES ('24321', '3', '0', 'Even now, in our darkest hour, they mock us!', '14', '0', '100', '22', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24321', '4', '0', 'Where are the titans in our time of greatest need? Our women birth abberations - disfigured runts unable to even stand on their own! Weak and ugly... Useless...', '14', '0', '100', '22', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24321', '5', '0', 'Ymiron has toiled. Long have I sat upon my throne and thought hard of our plight. There is only one answer... one reason...', '14', '0', '100', '22', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24321', '6', '0', 'For who but the titans themselves could bestow such a curse? What could have such power?', '14', '0', '100', '1', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24321', '7', '0', 'And the answer is nothing... For it is the titans that have cursed us!', '14', '0', '100', '53', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24321', '8', '0', 'The crowd cheers.', '16', '0', '100', '0', '0', '0', 'King Ymiron');
INSERT INTO `creature_text` VALUES ('24321', '9', '0', 'On this day all Vrykul will shed their old beliefs! We denounce our old gods! All Vrykul will pledge their allegiance to Ymiron! Ymiron will protect our noble race!', '14', '0', '100', '53', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24321', '10', '0', 'And now my first decree upon the Vrykul! All malformed infants born of Vrykul mother and father are to be destroyed upon birth! Our blood must remain pure always! Those found in violation of Ymiron\'s decree will be taken to Gjalerbron for execution!', '14', '0', '100', '1', '0', '0', 'King Ymiron yell');
INSERT INTO `creature_text` VALUES ('24322', '0', '0', 'Silence!', '14', '0', '100', '1', '0', '0', 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES ('24322', '1', '0', 'Show them mercy, my king! They are of our flesh and blood!', '14', '0', '100', '53', '0', '0', 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES ('24322', '2', '0', 'All hail our glorious king, Ymiron!', '14', '0', '100', '53', '0', '0', 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES ('24323', '0', '0', 'Show the abberations no mercy, Ymiron!', '14', '0', '100', '1', '0', '0', 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES ('24323', '1', '0', 'They weaken us! Our strength is dilluted by their very existence! Destroy them all!', '14', '0', '100', '113', '0', '0', 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES ('24366', '0', '0', 'Guards!', '14', '7', '100', '0', '0', '0', 'Nizzle');
INSERT INTO `creature_text` VALUES ('24528', '0', '0', 'How much\'ll it cost me to get my mules shod?', '12', '7', '100', '6', '0', '0', 'Brune Grayblade');
INSERT INTO `creature_text` VALUES ('24528', '1', '0', 'You don\'t understand! If I\'m not ready to go soon, all the good homesteading land will be taken!', '12', '7', '100', '1', '0', '0', 'Brune Grayblade');
INSERT INTO `creature_text` VALUES ('24528', '2', '0', 'That, sir, is a mule!', '12', '7', '100', '25', '0', '0', 'Brune Grayblade');
INSERT INTO `creature_text` VALUES ('24531', '0', '0', 'Yes, darling. You realize you\'ll be eating dire ram mutton for dinner until the crops are planted...', '12', '7', '100', '1', '0', '0', 'Gavin Ward');
INSERT INTO `creature_text` VALUES ('24531', '1', '0', 'I\'d rather listen to his braying than yours, dear.', '12', '7', '100', '0', '0', '0', 'Gavin Ward');
INSERT INTO `creature_text` VALUES ('24531', '2', '0', 'Of course, darling.', '12', '7', '100', '1', '0', '0', 'Gavin Ward');
INSERT INTO `creature_text` VALUES ('24532', '0', '0', 'And I don\'t want our land to have any of those horrible wolves, or giants, or ugly rams!', '12', '7', '100', '1', '0', '0', 'Sabetta Ward');
INSERT INTO `creature_text` VALUES ('24532', '1', '0', 'I\'d sooner butcher that mule of yours! Why did you bring that beast in here, anyway?', '12', '7', '100', '5', '0', '0', 'Sabetta Ward');
INSERT INTO `creature_text` VALUES ('24532', '2', '0', 'Remember, honey, we need to get a plot of land with a nice hot spring.', '12', '7', '100', '1', '0', '0', 'Sabetta Ward');
INSERT INTO `creature_text` VALUES ('24534', '0', '0', 'Welcome to Fort Wildervar, brave homesteaders! There\'s a whole continent out there just waiting to be claimed!', '12', '7', '100', '1', '0', '0', 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES ('24534', '1', '0', 'True, Northrend is a hard land, but our people are strong, hardy, and equal to the task!', '12', '7', '100', '0', '0', '0', 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES ('24534', '2', '0', 'We will win this land with the sword, and break it with the plow! You are the men and women who will be remembered for taming the wild continent!', '12', '7', '100', '1', '0', '0', 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES ('24534', '3', '0', 'But, you will not be alone out there. My men and I have prepared pack mules carrying the supplies you\'ll need most.', '12', '7', '100', '1', '0', '0', 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES ('24534', '4', '0', 'Axes, picks, seed, nails, food, blankets, water... it\'s all there, waiting for you. I think you\'ll find my prices quite reasonable, too.', '12', '7', '100', '25', '0', '0', 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES ('24534', '5', '0', 'There are more than enough to go around. Should you need other goods, don\'t hesitate to ask!', '12', '7', '100', '1', '0', '0', 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES ('24534', '6', '0', 'Now, my loyal custo... err, friends, go forth and conquer this land for our people!', '12', '7', '100', '274', '0', '0', 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES ('24639', '0', '0', 'Keep quiet, will you? If anyone catches on, we\'re both dead.', '15', '0', '100', '0', '0', '0', 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES ('24639', '1', '0', 'All right, fellas! Who ordered the spiced rum? Was it you, Jackie boy?', '12', '0', '100', '0', '0', '0', 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES ('24639', '2', '0', 'It\'s okay, sweetheart. This one\'s on the house.', '12', '0', '100', '0', '0', '0', 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES ('24710', '0', '0', 'Right! Let\'s try this so-called dire brew...', '12', '0', '100', '0', '0', '0', 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES ('24710', '1', '0', 'Bronzebeard\'s beard! What a drink!', '12', '0', '100', '0', '0', '0', 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES ('24710', '2', '0', '$N, this beer... well, it might be the best of Brewfest!', '12', '0', '100', '15', '0', '0', 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES ('24710', '3', '0', 'It is a shame Coren\'s Dire Brew can\'t quench more thirsting throats! It\'s blasphemy, I know, but I envy those Dark Iron dwarves!', '12', '0', '100', '274', '0', '0', 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES ('24710', '4', '0', 'Ho there, and welcome to Brewfest!', '12', '7', '100', '3', '0', '0', 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES ('24710', '4', '1', 'Hello, friend! Welcome to the Brewfest Grounds!', '12', '7', '100', '3', '0', '0', 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES ('24710', '4', '2', 'Hail, good $c! Welcome to Ironforge\'s annual Brewfest celebration!', '12', '7', '100', '3', '0', '0', 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES ('24711', '0', '0', 'Well time\'s money, and time\'s wasting! Let\'s have that drink...', '12', '0', '100', '1', '0', '0', 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES ('24711', '1', '0', 'Wow! This really greases the steamplugs!', '12', '0', '100', '1', '0', '0', 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES ('24711', '2', '0', 'If I were an honest goblin, I\'d say this was the best beer of the festival...', '12', '0', '100', '1', '0', '0', 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES ('24711', '3', '0', 'Too bad Coren\'s dead. If I could buy that brew from him and resell it - watered down and rebottled under my own label, of course - I\'d be rich!', '12', '0', '100', '1', '0', '0', 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES ('24711', '4', '0', 'Hey there, and welcome to Brewfest. Come in and have a drink or three!', '12', '1', '100', '3', '0', '0', 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES ('24711', '4', '1', 'What\'s up, friend? Welcome to the Brewfest Grounds! Now go buy some drinks!', '12', '1', '100', '3', '0', '0', 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES ('24711', '4', '2', 'Hey hey, it\'s another $r! Welcome to my annual Brewfest celebration... now go feast and drink some!', '12', '1', '100', '3', '0', '0', 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES ('24779', '0', '0', 'Tonight we feast on you!', '12', '0', '100', '0', '0', '0', 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES ('24779', '0', '1', 'Such easy prey...', '12', '0', '100', '0', '0', '0', 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES ('24779', '0', '2', 'Who\'s hunting who?', '12', '0', '100', '0', '0', '0', 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES ('24788', '0', '0', 'Sure thing, love. Put it on Harry\'s tab, will ya? He owes me a drink!', '12', '0', '100', '0', '0', '0', 'Jack Adams');
INSERT INTO `creature_text` VALUES ('24788', '1', '0', 'Free rum? Why... that\'s me favorite kind!', '12', '0', '100', '0', '0', '0', 'Jack Adams');
INSERT INTO `creature_text` VALUES ('24788', '2', '0', 'Sweet Neptulon! That was... one drink... too many!', '12', '0', '100', '0', '0', '0', 'Jack Adams');
INSERT INTO `creature_text` VALUES ('25288', '0', '0', 'Guards!', '14', '1', '100', '0', '0', '0', 'Turida Coldwind');
INSERT INTO `creature_text` VALUES ('25321', '0', '0', 'Thank you for freeing me! May the tides always favor you.', '12', '0', '100', '1', '0', '0', 'Kaskala Craftman');
INSERT INTO `creature_text` VALUES ('25321', '0', '1', 'Do not allow Kaskala to forget what has happened here.', '12', '0', '100', '1', '0', '0', 'Kaskala Craftman');
INSERT INTO `creature_text` VALUES ('25322', '0', '0', 'May the ancestors always aid you, $n, as you have aided me.', '12', '0', '100', '1', '0', '0', 'Kaskala Shaman');
INSERT INTO `creature_text` VALUES ('25322', '0', '1', 'Thank you, $n. May the winds and seas always deliver you safely.', '12', '0', '100', '1', '0', '0', 'Kaskala Shaman');
INSERT INTO `creature_text` VALUES ('25678', '0', '0', 'Ah, good... more parts!', '14', '0', '100', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25678', '0', '1', 'Clear!', '14', '0', '100', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25678', '0', '2', 'Fresh meat!', '14', '0', '100', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25678', '0', '3', 'I recommened evisceration!', '14', '0', '100', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25678', '0', '4', 'It\'s no good... you need more work first.', '14', '0', '100', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25678', '0', '5', 'Live, damn you!', '14', '0', '100', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25678', '0', '6', 'The doctor is in!', '14', '0', '100', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25753', '0', '0', 'Warning! Warning! Intruder alert! Intruder alert!', '12', '0', '100', '0', '0', '0', 'Sentry-bot 57-K');
INSERT INTO `creature_text` VALUES ('25753', '0', '1', 'You have been detected. You will be assimilated or eliminated.', '12', '0', '100', '0', '0', '0', 'Sentry-bot 57-K');
INSERT INTO `creature_text` VALUES ('25753', '0', '2', 'Activate counter-measures. Repel intruder.', '12', '0', '100', '0', '0', '0', 'Sentry-bot 57-K');
INSERT INTO `creature_text` VALUES ('25758', '0', '0', 'Incoming $r flier! Shoot $g him:her; down!', '12', '0', '100', '0', '0', '0', 'Defendo-tank 66D');
INSERT INTO `creature_text` VALUES ('25773', '0', '1', 'I survived and I\'m flesh again!', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25773', '0', '2', 'But... but... I was perfect!', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25773', '0', '3', 'Where am I? Who are you? What\'s that strange feeling?', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25773', '0', '4', 'I\'m flesh and blood again! Thank you!', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25783', '0', '0', 'I\'m flesh and blood again. That\'s all that matters!', '12', '7', '100', '5', '0', '0', 'Fizzcrank Survivor');
INSERT INTO `creature_text` VALUES ('25814', '1', '1', 'We are Mechagnome...resistance is futile.', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25814', '1', '2', 'The flesh is weak. We will make you better, stronger...faster.', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25814', '1', '3', 'We can decurse you, we have the technology.', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('26073', '0', '0', 'Who disturbs my meditation?!', '14', '7', '100', '15', '0', '0', 'High Priest Talet-Kha');
INSERT INTO `creature_text` VALUES ('26293', '0', '0', 'The Hulking Jormungar falters for a moment, opening its mouth wide.', '41', '0', '100', '0', '0', '0', 'Hulking Jormungar');
INSERT INTO `creature_text` VALUES ('26420', '0', '0', 'Ah, yes. Loken is well known to me.', '12', '0', '100', '0', '0', '0', 'Gavrock');
INSERT INTO `creature_text` VALUES ('26420', '1', '0', 'It is he who commands the sons of iron in their war against us.', '12', '0', '100', '0', '0', '0', 'Gavrock');
INSERT INTO `creature_text` VALUES ('26420', '2', '0', 'From his hiding place, he oversees their preparations for war with the goal of exterminating the stone giants!', '12', '0', '100', '0', '0', '0', 'Gavrock');
INSERT INTO `creature_text` VALUES ('26484', '0', '0', 'He\'s out to get me, too! I\'d be careful around here if I was you, traveler. You never know which tree he\'s hiding behind!', '12', '0', '100', '0', '0', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '1', '0', 'That\'s not something Limpy Joe would ask! But yeah, I know Loken.', '12', '0', '100', '0', '0', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26537', '0', '0', 'The zeppelin to Warsong Hold has just arrived! All aboard for Borean Tundra!', '14', '0', '100', '0', '0', '0', 'Greeb Ramrocket');
INSERT INTO `creature_text` VALUES ('26566', '0', '0', 'Guards!', '14', '1', '100', '0', '0', '0', 'Narzun Skybreaker');
INSERT INTO `creature_text` VALUES ('26602', '0', '0', 'Guards!', '14', '7', '100', '0', '0', '0', 'Kara Thricestar');
INSERT INTO `creature_text` VALUES ('26668', '0', '0', 'The sensation is... beyond my imagining. I am yours to command, my king.', '14', '0', '0', '0', '0', '13857', 'Svala Sorrowgrave SAY_DIALOG_WITH_ARTHAS_2');
INSERT INTO `creature_text` VALUES ('26668', '1', '0', 'I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King!', '14', '0', '0', '0', '0', '13858', 'Svala Sorrowgrave SAY_DIALOG_WITH_ARTHAS_3');
INSERT INTO `creature_text` VALUES ('26668', '2', '0', 'I will vanquish your soul!', '14', '0', '0', '0', '0', '13842', 'Svala Sorrowgrave SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26668', '3', '0', 'You were a fool to challenge the power of the Lich King!', '14', '0', '0', '0', '0', '13845', 'Svala Sorrowgrave SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26668', '3', '1', 'Your will is done, my king.', '14', '0', '0', '0', '0', '13847', 'Svala Sorrowgrave SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26668', '3', '2', 'Another soul for my master.', '14', '0', '0', '0', '0', '13848', 'Svala Sorrowgrave SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26668', '4', '0', 'Nooo! I did not come this far... to...', '14', '0', '0', '0', '0', '13855', 'Svala Sorrowgrave SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26668', '5', '0', 'Your death approaches.', '14', '0', '0', '0', '0', '13850', 'Svala Sorrowgrave SAY_SACRIFICE_1');
INSERT INTO `creature_text` VALUES ('26668', '5', '1', 'Go now to my master.', '14', '0', '0', '0', '0', '13851', 'Svala Sorrowgrave SAY_SACRIFICE_2');
INSERT INTO `creature_text` VALUES ('26668', '5', '2', 'Your end is inevitable.', '14', '0', '0', '0', '0', '13852', 'Svala Sorrowgrave SAY_SACRIFICE_3');
INSERT INTO `creature_text` VALUES ('26668', '5', '3', 'Yor-guul mak!', '14', '0', '0', '0', '0', '13853', 'Svala Sorrowgrave SAY_SACRIFICE_4');
INSERT INTO `creature_text` VALUES ('26668', '5', '4', 'Any last words?', '14', '0', '0', '0', '0', '13854', 'Svala Sorrowgrave SAY_SACRIFICE_5');
INSERT INTO `creature_text` VALUES ('26817', '0', '0', 'I\'ll blast those gnomish wannabes back to the scrap heap!', '12', '7', '100', '0', '0', '0', 'Fizzcrank Fighter');
INSERT INTO `creature_text` VALUES ('26817', '0', '1', 'You\'re sending me back there?!', '12', '7', '100', '0', '0', '0', 'Fizzcrank Fighter');
