INSERT INTO `creature_text` VALUES ('26917', '0', '0', 'Mortal champions have long used these weapons to combat evil. I see no reason to keep the swords from them in this battle.', '12', '0', '100', '1', '0', '0', 'Alexstrasza the Life-Binder');
INSERT INTO `creature_text` VALUES ('27113', '0', '0', 'Thank you for rescuing me, but the others are still held on the tiers above!', '12', '0', '100', '1', '0', '0', 'Prospector Gann');
INSERT INTO `creature_text` VALUES ('27114', '0', '0', 'Thank goodness! I was worried they were going to press me into service in their war against the stone giants!', '12', '0', '100', '1', '0', '0', 'Prospector Torgan');
INSERT INTO `creature_text` VALUES ('27115', '0', '0', 'These irons are crazy! They keep talking about serving their \'master Loken\' but from what I can tell, only a few receive orders from him.', '12', '0', '100', '1', '0', '0', 'Prospector Varana');
INSERT INTO `creature_text` VALUES ('27234', '0', '0', 'You are being misled! The Onslaught is all lies! The Scourge and the Forsaken are not our enemies! Wake up!', '14', '0', '100', '5', '0', '0', 'Blacksmith Goodman');
INSERT INTO `creature_text` VALUES ('27235', '0', '0', 'Renounce the Scarlet Onslaught! Don\'t listen to the lies of the high general and the grand admiral any longer!', '14', '0', '100', '5', '0', '0', 'Lead Cannoneer Zierhut');
INSERT INTO `creature_text` VALUES ('27236', '0', '0', 'Abbendis is nothing but a harlot and Grand Admiral Westwind is selling her cheap like he sold us out!', '14', '0', '100', '5', '0', '0', 'Stable Master Mercer');
INSERT INTO `creature_text` VALUES ('27237', '0', '0', 'High general Abbendis personally told me that it was a mistake to come north and that we\'re doomed! I urge you all to lay down your weapons and leave before it\'s too late!', '14', '0', '100', '5', '0', '0', 'Commander Jordan');
INSERT INTO `creature_text` VALUES ('27316', '0', '0', 'Stand back, $N, the beast might lash out and harm you.', '12', '7', '100', '396', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '1', '0', 'GODFREY! Hear me, fiend! Hear me, for I am the Light, here to deliver you from evil!', '12', '7', '100', '5', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '2', '0', 'Good. I have your attention, then, Godfrey?', '12', '7', '100', '396', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '3', '0', 'We can do this in one of two ways, Godfrey. First, I will simply ask you to tell me what the unholy markings etched upon the pages of this tome mean. What say you?', '12', '7', '100', '1', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '4', '0', 'Then it is option two.', '12', '7', '100', '1', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '5', '0', 'I say a prayer for you now, lost soul. May the Light take you gracefully.', '12', '7', '100', '68', '4000', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '6', '0', 'Let the sermon begin.', '12', '7', '100', '1', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '7', '0', 'LIGHT TAKE YOU, BEAST!', '12', '7', '100', '0', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '8', '0', 'BURN IN HOLY FIRE!', '12', '7', '100', '0', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '9', '0', 'LET THE LIGHT IN, GODFREY GOODARD!', '12', '7', '100', '0', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '10', '0', 'May the power of Light compel you!', '12', '7', '100', '0', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '11', '0', 'I thought you would see the Light, Godfrey... Now speak quickly, fiend. What does it say?', '12', '7', '100', '396', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '12', '0', 'RUBBISH! Godfrey do you take me for a fool? Do not spew your Scourge propaganda at a man of the Light, heathen! Speak now or I will have the peasants craft a holy water bath and dip you into it like a dog with fleas!', '12', '7', '100', '6', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '13', '0', 'What? There is a mausoleum beneath the old village? What fools we are to not have noticed! We will find and strike down your master now, Godfrey. We will end this nightmare.', '12', '7', '100', '6', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '14', '0', 'You let us worry about how we kill the monster, Godfrey.', '12', '7', '100', '396', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '15', '0', 'Return to Halford with the information that the good mayor was kind enough to submit, $N. I will finish here and squeeze whatever else this wretch might know about Thel\'zan. Now, I will show Godfrey the rarely seen \"option three.\"', '12', '7', '100', '1', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27316', '16', '0', 'Let\'s you and I have a chat about some things, Godfrey.', '12', '7', '100', '1', '0', '0', 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES ('27344', '0', '0', 'Guards!', '14', '1', '100', '0', '0', '0', 'Bat Handler Adeline');
INSERT INTO `creature_text` VALUES ('27430', '0', '0', 'The Destructive Ward gains in power.', '41', '0', '100', '0', '0', '0', 'Destructive Ward');
INSERT INTO `creature_text` VALUES ('27430', '1', '0', 'The Destructive Ward is fully charged!', '41', '0', '100', '0', '0', '0', 'Destructive Ward');
INSERT INTO `creature_text` VALUES ('27447', '0', '0', 'There will be no mercy!', '14', '0', '0', '0', '0', '13649', '');
INSERT INTO `creature_text` VALUES ('27447', '1', '1', 'Blast them! Destroy them!', '14', '0', '0', '0', '0', '13650', '');
INSERT INTO `creature_text` VALUES ('27447', '2', '2', '%s calls an Azure Ring Captain!', '41', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('27447', '3', '3', 'They are... too strong! Underestimated their... fortitude.', '14', '0', '0', '0', '0', '13655', '');
INSERT INTO `creature_text` VALUES ('27577', '0', '0', '%s growls.', '16', '0', '100', '0', '0', '409', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '1', '0', '%s hisses!', '16', '0', '100', '0', '0', '410', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '2', '0', 'Tell you nothing, preacher.', '12', '0', '100', '1', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '3', '0', 'What can you do to me that Kel\'Thuzad has not? That the Lich King will not?', '12', '0', '100', '1', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '4', '0', 'The book is your salvation, yes... but nothing will you know! NOTHING I SAY! NOTHING! ', '12', '0', '100', '1', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '5', '0', 'No more LIGHT! NO MORE! I BEG YOU!', '12', '0', '100', '0', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '6', '0', 'Never felt hurt like this!', '12', '0', '100', '0', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '7', '0', 'It tells of the coming apocalypse. How this world will burn and be reborn from death.', '12', '0', '100', '1', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '8', '0', 'NOOOO!!! I tell you! I tell you everything!', '12', '0', '100', '39', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '9', '0', 'Humans... Beneath the earth of Wintergarde Village you built a mausoleum! Why do you think Naxxramas attacked that spot? Thel\'zan hides deep in your own crypt and sends a thousand-thousand corpses at you! Perish you will...', '12', '0', '100', '1', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '10', '0', 'How? Humans truly are stupid, yes? Thel\'zan the Duskbringer! Thel\'zan the Lich! He who is born of human flesh and bone, sacrificed all for power, protected by the Lich King!', '12', '0', '100', '1', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27577', '11', '0', 'You cannot stop Thel\'zan! He bears the dark gift, bestowed upon him by the Lich King himself!', '12', '0', '100', '1', '0', '0', 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES ('27656', '4', '0', 'Savor this small victory, foolish little creatures. You and your dragon allies have won this battle, but we will win... the Nexus War.', '14', '0', '100', '0', '0', '13631', 'Ley-Guardian Eregos - SAY_DEATH');
INSERT INTO `creature_text` VALUES ('27656', '5', '0', 'We command the arcane! It shall not be used against us.', '14', '0', '100', '0', '0', '13626', 'Ley-Guardian Eregos - SAY_SHIELD');
INSERT INTO `creature_text` VALUES ('27842', '0', '0', 'Bat gizzards again for the gnomes tonight...', '12', '0', '100', '1', '0', '0', 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES ('27842', '0', '1', 'What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get.', '12', '0', '100', '1', '0', '0', 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES ('27842', '0', '2', 'We like trees, Fenrick. They provide cover. They won\'t let me chop them down, either.', '12', '0', '100', '1', '0', '0', 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES ('27842', '0', '3', 'I wonder how many reinforcements need to suffer injury before they allows us to chop down these idiotic trees. They\'re costing us a fortune in bats. Maybe I\'ll rig a harness or two...', '12', '0', '100', '1', '0', '0', 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES ('27842', '1', '0', 'Arrrhhh...Guards!', '14', '7', '100', '0', '0', '0', 'Common Horde Flight Master');
INSERT INTO `creature_text` VALUES ('27915', '0', '0', 'Good work with the crates! Come talk to me in front of Stratholme for your next assignment!', '15', '0', '0', '0', '0', '0', 'Chromie - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES ('27990', '0', '0', 'You\'re too late, $n. Another visitor from Dalaran came asking after information about the same prismatic dragon blades.', '12', '0', '100', '1', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('27990', '1', '0', 'From your description, I\'m certain the book I loaned our visitor could allow you to easily identify the weapon.', '12', '0', '100', '25', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('27990', '2', '0', 'I\'m afraid you\'ll have to ask the -- Well, perhaps Kalecgos can help.', '12', '0', '100', '1', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('27990', '3', '0', '$n may have found the remains of a prismatic blade, Kalecgos. Will you offer your help to our visitor?', '12', '0', '100', '1', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('27990', '4', '0', 'You believe our allies will not be able to control the power of the swords?', '12', '0', '100', '1', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('27990', '5', '0', 'As will we all.', '12', '0', '100', '25', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('27990', '6', '0', 'Please, mortal, speak with Arcanist Tybalin in Dalaran. He may be able to negotiate with the Sunreavers for access to the book.', '12', '0', '100', '1', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('27990', '7', '0', 'Please, mortal, seek out Magister Hathorel in Dalaran. He might be able to negotiate with the Silver Covenant for access to the book.', '12', '0', '100', '1', '0', '0', 'Krasus');
INSERT INTO `creature_text` VALUES ('28012', '0', '0', 'The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearences! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above.', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('28012', '1', '1', 'Your greatest challenge lies ahead. Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring.', '12', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('28179', '0', '0', 'The Lich King reacted swiftly to the breach. Faster than I anticipated.', '12', '0', '100', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('28179', '1', '0', 'What would you have me do, Darion?', '12', '0', '100', '6', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('28179', '2', '0', 'Choose your words wisely, death knight. You stand amidst the company of the devoted.', '12', '0', '100', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('28179', '3', '0', 'We will do this with honor, Darion. We will not sink to the levels of the Scourge to be victorious. To do so would make us no better than the monster that we fight to destroy!', '12', '0', '100', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('28518', '0', '0', 'Not sure how ol\' Nass always finds his way back here. Must be by smell....', '12', '0', '100', '0', '0', '0', 'Stefan Vadu');
INSERT INTO `creature_text` VALUES ('28521', '0', '0', 'Let\'s go to Zul\'Drak he says. We can get to the bottom of this mess, he says...', '12', '0', '33', '0', '0', '0', 'Nass');
INSERT INTO `creature_text` VALUES ('28521', '0', '1', 'One more flock of scourge hair gained, one more shred of dignity lost...', '12', '0', '33', '0', '0', '0', 'Nass');
INSERT INTO `creature_text` VALUES ('28521', '0', '2', 'Are we finished yet?', '12', '0', '33', '0', '0', '0', 'Nass');
INSERT INTO `creature_text` VALUES ('28532', '0', '0', 'Indeed. Watch this, $r.', '12', '0', '100', '1', '0', '0', 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES ('28532', '1', '0', 'Here, troll... a gift!', '12', '0', '100', '0', '0', '0', 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES ('28532', '2', '0', 'Ugh... disgusting!', '12', '0', '100', '0', '0', '0', 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES ('28541', '0', '0', 'For me? Really, mon?', '12', '0', '100', '6', '0', '0', 'Captured Drakkari Scout');
INSERT INTO `creature_text` VALUES ('28541', '1', '0', 'It....it be beautiful!', '12', '0', '100', '5', '0', '0', 'Captured Drakkari Scout');
INSERT INTO `creature_text` VALUES ('28546', '0', '0', 'You wish to confront the master? You must first weather the storm!', '14', '0', '100', '0', '0', '14453', 'Ionar');
INSERT INTO `creature_text` VALUES ('28546', '1', '0', 'The slightest spark shall be your undoing.', '14', '0', '100', '0', '0', '14454', 'Ionar');
INSERT INTO `creature_text` VALUES ('28546', '1', '1', 'No one is safe!', '14', '0', '100', '0', '0', '14455', 'Ionar');
INSERT INTO `creature_text` VALUES ('28546', '2', '0', 'Shocking ... I know!', '14', '0', '100', '0', '0', '14456', 'Ionar');
INSERT INTO `creature_text` VALUES ('28546', '2', '1', 'You atempt the unpossible.', '14', '0', '100', '0', '0', '14457', 'Ionar');
INSERT INTO `creature_text` VALUES ('28546', '2', '2', 'Your spark of light is ... extinguish.', '14', '0', '100', '0', '0', '14458', 'Ionar');
INSERT INTO `creature_text` VALUES ('28546', '3', '0', 'Master... you have guests.', '14', '0', '100', '0', '0', '14459', 'Ionar');
INSERT INTO `creature_text` VALUES ('28563', '0', '0', 'The structure above begins to hum with energy.', '42', '0', '0', '0', '0', '0', 'Freya\'s Presence - On Call of the Lifewarden cast');
INSERT INTO `creature_text` VALUES ('28566', '0', '0', 'Beginning the distillation in 5 seconds.', '12', '0', '100', '0', '0', '0', 'Tipsy McManus');
INSERT INTO `creature_text` VALUES ('28566', '1', '0', 'Add another orange, quickly!', '12', '0', '100', '25', '0', '0', 'Tipsy McManus');
