INSERT INTO `creature_text` VALUES ('28601', '0', '0', 'Fool! You led us to the only being that could stand up to our armies! You will never bring the Etymidian back to Northrend!', '12', '0', '100', '25', '0', '0', 'High Cultist Herenn');
INSERT INTO `creature_text` VALUES ('28859', '10', '0', 'Now your benefactors make their appearance... But they are too late. The powers contained here are sufficient to destroy the world ten times over! What do you think they will do to you?', '14', '0', '100', '0', '13000', '14530', 'Intro Phase 3');
