INSERT INTO `creature_text` VALUES ('30190', '8', '0', 'But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure.', '12', '0', '100', '1', '0', '0', 'Attendant Tock');
