INSERT INTO `creature_text` VALUES ('30225', '0', '0', 'Eversong Woods, Dalfors. I\'m thinking about how beautiful it was before Arthas cut a swathe of death through it...', '12', '0', '100', '1', '0', '0', 'Crusader Sunborn');
