INSERT INTO `creature_text` VALUES ('40329', '0', '0', 'Dat be enough for now!', '12', '0', '100', '25', '0', '0', 'Zen\'tabra');
INSERT INTO `creature_text` VALUES ('40329', '1', '0', 'Don\'t be lookin\' so surprised. Your shaman friend has sharp eyes, or should I say, sharp frogs.', '12', '0', '100', '1', '0', '0', 'Zen\'tabra');
INSERT INTO `creature_text` VALUES ('40329', '2', '0', 'My kind, da druids, we been layin\' low for some time now. We been waitin\' to see when an\' if Vol\'jin might return.', '12', '0', '100', '11', '0', '0', 'Zen\'tabra');
INSERT INTO `creature_text` VALUES ('40329', '3', '0', 'Now dat it looks like de Darkspear be returnin\' to these isles, maybe de time has come for us to reveal ourselves.', '12', '0', '100', '1', '0', '0', 'Zen\'tabra');
INSERT INTO `creature_text` VALUES ('40329', '4', '0', 'Go back to Vol\'jin an\' tell him dis: Zen\'tabra stands ready to help him in de coming battle.', '12', '0', '100', '1', '0', '0', 'Zen\'tabra');
INSERT INTO `creature_text` VALUES ('40352', '0', '0', 'Darkspears, we consult de spirits! Drummers, take your places!', '12', '0', '100', '25', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '1', '0', 'Spirits, we be gathered here to ask for your guidance.', '12', '0', '100', '5', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '2', '0', 'Our leader, Vol\'jin, son of Sen\'jin, issued de call to all Darkspears: reclaim de Echo Isles for our tribe.', '12', '0', '100', '1', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '3', '0', 'Spirits! I offer me own body to you! Speak through me! Is de time right for mighty Vol\'jin\'s undertaking?', '12', '0', '100', '5', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '4', '0', 'De ancestors hear ya, witch doctor!', '12', '0', '100', '1', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '5', '0', 'Know dat your plans please us, Darkspears. De son of Sen\'jin walks de right path.', '12', '0', '100', '1', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '6', '0', 'De task in front of ya will not be easy, but ya have our blessin\'.', '12', '0', '100', '1', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '7', '0', 'Ya gave up your home an\' ya gave up de loas of your ancestors when ya left de Echo Isles. Dey will not be pleased dat you been ignorin\' dem.', '12', '0', '100', '1', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '8', '0', 'Ya must make amends wit\' Bwonsamdi, de guardian of de dead, if ya hope to defeat Zalazane. It be de only way. Tell de son of Sen\'jin dese things.', '12', '0', '100', '1', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40352', '9', '0', 'De spirits have blessed us wit\' an answer! De Echo Isles will be ours again!', '12', '0', '100', '5', '0', '0', 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES ('40391', '0', '0', 'Thank ya, scout. Keep up da patrols. But for now, a rest is in order. Dismissed.', '12', '0', '100', '1', '0', '0', 'Vol\'jin');
INSERT INTO `creature_text` VALUES ('40416', '0', '0', 'He got a big army, an\' he be plannin\' somethin\' down dere.', '12', '0', '100', '1', '0', '0', 'Darkspear Scout');
INSERT INTO `creature_text` VALUES ('40416', '0', '1', 'Zalazane got most of his hexes trolls hidden under de canopy on de big island.', '12', '0', '100', '1', '0', '0', 'Darkspear Scout');
INSERT INTO `creature_text` VALUES ('40429', '0', '0', 'Thank you! I could not have held out for much longer.... A terrible thing has happened here.', '14', '0', '100', '5', '0', '17491', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '1', '0', 'We believed the Sanctum was well-fortified, but we were not prepared for the nature of this assault.', '12', '0', '100', '1', '0', '17492', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '2', '0', 'The Black dragonkin materialized from thin air, and set upon us before we could react.', '12', '0', '100', '1', '0', '17493', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '3', '0', 'We did not stand a chance. As my brethren perished around me, I managed to retreat here and bar the entrance.', '12', '0', '100', '1', '0', '17494', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '4', '0', 'They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the Sanctum.', '12', '0', '100', '1', '0', '17495', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '5', '0', 'The commander of the forces on the ground here is a cruel brute named Zarithrian, but I fear there are greater powers at work.', '12', '0', '100', '1', '0', '17496', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '6', '0', 'In their initial assault, I caught a glimpse of their true leader, a fearsome full-grown twilight dragon.', '12', '0', '100', '1', '0', '17497', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '7', '0', 'I know not the extent of their plans, heroes, but I know this:  They cannot be allowed to succeed!', '12', '0', '100', '5', '0', '17498', 'Sanctum Guardian Xerestrasza');
INSERT INTO `creature_text` VALUES ('40429', '8', '0', 'Help! I am trapped within this tree!  I require aid!', '14', '0', '100', '5', '0', '17490', 'Sanctum Guardian Xerestrasza');
