INSERT INTO `creature_text` VALUES ('29032', '16', '0', '$N, I\'d recognize those horns anywhere... What have they done to you, $N?', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_RECOG_5');
INSERT INTO `creature_text` VALUES ('29032', '17', '0', '$N, I\'d recognize dem tusks anywhere... What... What have dey done ta you, mon?', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_RECOG_6');
INSERT INTO `creature_text` VALUES ('29032', '18', '0', 'You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a righteous force of reckoning. Every last ounce of good... Everything that made you a draenei!', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_NOREM_1');
INSERT INTO `creature_text` VALUES ('29032', '19', '0', 'Ye don\'t remember me, do ye? Blasted Scourge... They\'ve tried to drain ye o\' everything that made ye a righteous force o\' reckoning. Every last ounce o\' good... Everything that made you a $Gson:daughter; of Ironforge!', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_NOREM_2');
INSERT INTO `creature_text` VALUES ('29032', '20', '0', 'You don\'t remember me, do you? We were humans once - long, long ago - until Lordaeron fell to the Scourge. Your transformation to a Scourge zombie came shortly after my own. Not long after that, our minds were freed by the Dark Lady.', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_NOREM_3');
INSERT INTO `creature_text` VALUES ('29032', '21', '0', 'You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a pint-sized force of reckoning. Every last ounce of good... Everything that made you a gnome!', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_NOREM_4');
INSERT INTO `creature_text` VALUES ('29032', '22', '0', 'You don\'t remember me, do you? Blasted Scourge...They\'ve tried to drain of everything that made you a righteous force of reckoning. Every last ounce of good...Everything that made you a human!', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_NOREM_5');
INSERT INTO `creature_text` VALUES ('29032', '23', '0', 'You don\'t remember me? When you were a child your mother would leave you in my care while she served at the Temple of the Moon. I held you in my arms and fed you with honey and sheep\'s milk to calm you until she would return. You were my little angel. Blasted Scourge... What have they done to you, $N?', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_NOREM_6');
INSERT INTO `creature_text` VALUES ('29032', '24', '0', 'You don\'t recognize me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a righteous force of reckoning. Every last ounce of good... Everything that made you an orc!', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_NOREM_7');
