INSERT INTO `creature_text` VALUES ('29178', '49', '0', 'ATTACK!!!', '14', '0', '100', '0', '0', '14488', 'Lord Maxwell Tyrosus');
INSERT INTO `creature_text` VALUES ('29183', '50', '0', 'APOCALYPSE!', '14', '0', '100', '0', '0', '14808', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29173', '51', '0', 'That day is not today...', '12', '0', '100', '0', '0', '14708', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29173', '52', '0', 'Tirion!', '14', '0', '100', '0', '0', '14709', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29175', '53', '0', 'ARTHAS!!!!', '14', '0', '100', '0', '0', '14591', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29183', '54', '0', 'What is this?', '14', '0', '100', '0', '0', '14809', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29175', '55', '0', 'Your end.', '14', '0', '100', '0', '0', '14592', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29183', '56', '0', 'Impossible...', '14', '0', '100', '0', '0', '14810', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29183', '57', '0', 'This... isn\'t... over...', '14', '0', '100', '0', '0', '14811', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29183', '58', '0', 'When next we meet it won\'t be on holy ground, paladin.', '14', '0', '100', '0', '0', '14812', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29175', '59', '0', 'Rise, Darion, and listen...', '12', '0', '100', '0', '0', '14593', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '60', '0', 'We have all been witness to a terrible tragedy. The blood of good men has been shed upon this soil! Honorable knights, slain defending their lives - our lives!', '12', '0', '100', '0', '0', '14594', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '61', '0', 'And while such things can never be forgotten, we must remain vigilant in our cause!', '12', '0', '100', '0', '0', '14595', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '62', '0', 'The Lich King must answer for what he has done and must not be allowed to cause further destruction to our world.', '12', '0', '100', '0', '0', '14596', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '63', '0', 'I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union.', '12', '0', '100', '0', '0', '14597', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '64', '0', 'The Argent Dawn and the Order of the Silver Hand will come together as one! We will succeed where so many before us have failed!', '12', '0', '100', '0', '0', '14598', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29228', '39', '0', 'If I die, father, I would rather it be on my feet, standing in defiance against the undead legions! If I die, father, I die with you!', '12', '0', '100', '0', '0', '14706', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '40', '0', 'My son, there will come a day when you will command the Ashbringer and, with it, mete justice across this land. I have no doubt that when that day finally comes, you will bring pride to our people and that Lordaeron will be a better place because of you. But, my son, that day is not today.', '12', '0', '100', '0', '0', '14496', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29173', '8', '12', 'What is happening to me?', '12', '0', '100', '0', '0', '14695', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29173', '8', '13', 'Power...wanes...', '12', '0', '100', '0', '0', '14696', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29173', '8', '14', 'Ashbringer defies me...', '12', '0', '100', '0', '0', '14697', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29173', '8', '15', 'Minions, come to my aid!', '12', '0', '100', '0', '0', '14698', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29175', '24', '0', 'You cannot win, Darion!', '14', '0', '100', '0', '0', '14584', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '25', '0', 'Bring them before the chapel!', '14', '0', '100', '0', '0', '14585', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29173', '26', '0', 'Stand down, death knights. We have lost... The Light... This place... No hope...', '12', '0', '100', '0', '0', '14699', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29175', '27', '0', 'Have you learned nothing, boy? You have become all that your father fought against! Like that coward, Arthas, you allowed yourself to be consumed by the darkness...the hate... Feeding upon the misery of those you tortured and killed!', '12', '0', '100', '0', '0', '14586', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '28', '0', 'Your master knows what lies beneath the chapel. It is why he dares not show his face! He\'s sent you and your death knights to meet their doom, Darion.', '12', '0', '100', '0', '0', '14587', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '29', '0', 'What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion!', '12', '0', '100', '0', '0', '14588', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29173', '30', '0', 'Save your breath, old man. It might be the last you ever draw.', '12', '0', '100', '0', '0', '14700', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '31', '0', 'My son! My dear, beautiful boy!', '12', '0', '100', '0', '0', '14493', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29173', '32', '0', 'Father!', '12', '0', '100', '0', '0', '14701', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29173', '33', '0', 'Argh...what...is...', '12', '0', '100', '0', '0', '14702', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29228', '34', '0', 'Father, you have returned!', '12', '0', '100', '0', '0', '14703', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29228', '35', '0', 'You have been gone a long time, father. I thought...', '12', '0', '100', '0', '0', '14704', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '36', '0', 'Nothing could have kept me away from here, Darion. Not from my home and family.', '12', '0', '100', '0', '0', '14494', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29228', '37', '0', 'Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer!', '12', '0', '100', '0', '0', '14705', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '38', '0', 'Darion Mograine, you are barely of age to hold a sword, let alone battle the undead hordes of Lordaeron! I couldn\'t bear losing you. Even the thought...', '12', '0', '100', '0', '0', '14495', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29183', '84', '0', '%s disappears. Tirion walks over to where Darion lay', '16', '0', '100', '0', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29175', '83', '0', '%s charges towards the Lich King, Ashbringer in hand and strikes the Lich King.', '16', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29173', '82', '0', '%s collapses.', '16', '0', '100', '0', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29228', '75', '0', '%s hugs his father.', '16', '0', '100', '0', '0', '0', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29175', '65', '0', 'We will take the fight to Arthas and tear down the walls of Icecrown!', '12', '0', '100', '0', '0', '14599', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '66', '0', 'The Argent Crusade comes for you, Arthas!', '14', '0', '100', '0', '0', '14600', 'Highlord Tirion Fordring');
