INSERT INTO `creature_text` VALUES ('29065', '27', '0', 'A pact was made, $Gbrother:sister;! We vowed vengeance against the Lich King! For what he had done to us! We battled the Scourge as Forsaken, pushing them back into the plaguelands and freeing Tirisfal! You and I were champions of the Forsaken!', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_THINK_1');
