INSERT INTO `creature_text` VALUES ('29065', '29', '0', 'Think, $N. Think back. Try and remember the majestic halls of Silvermoon City, where you were born. Remember the splendor of life, $Gbrother:sister;. You were a champion of the sin\'dorei once! This isn\'t you.', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_THINK_3');
INSERT INTO `creature_text` VALUES ('29065', '30', '0', 'Think, $N. Think back. Try and remember the proud mountains of Argus, where you were born. Remember the splendor of life, $Gbrother:sister;. You were a champion of the draenei once! This isn\'t you.', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_THINK_4');
INSERT INTO `creature_text` VALUES ('29065', '31', '0', 'Think, $N. Think back. Try and remember the snow capped mountains o\' Dun Morogh! Ye were born there, $Glad:lass;. Remember the splendor o\' life, $N! Ye were a champion o\' the dwarves once! This isn\'t ye!', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_THINK_5');
INSERT INTO `creature_text` VALUES ('29065', '32', '0', 'Think, $N. Think back. Try and remember Gnomeregan before those damned troggs! Remember the feel of an [arclight spanner] $Gbrother:sister;. You were a champion of gnome-kind once! This isn\'t you.', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_THINK_6');
INSERT INTO `creature_text` VALUES ('29065', '33', '0', 'Think, $N. Think back. Try and remember the hills and valleys of Elwynn, where you were born. Remember the splendor of life, $Gbrother:sister;. You were a champion of the Alliance once! This isn\'t you.', '12', '0', '100', '0', '0', '0', 'special_surprise SAY_EXEC_THINK_7');
