INSERT INTO `creature_text` VALUES ('30086', '1', '0', 'You fight well, little one. I am bested for now. We will meet again I assure you. When we do I will be better prepared!', '12', '0', '100', '66', '0', '0', 'say');
INSERT INTO `creature_text` VALUES ('37846', '8', '0', 'Arthas shattered our forces and took the most powerful of us into his service as the San\'layn.', '12', '0', '100', '1', '0', '16804', 'Blood Queen Lana\'thel - Say 8');
INSERT INTO `creature_text` VALUES ('37846', '7', '0', 'After the battle, I recovered the sword from the field. I bore it to Northrend in the service of my prince, seeking to avenge our people\'s defeat.', '12', '0', '100', '16', '0', '16803', 'Blood Queen Lana\'thel - Say 7');
INSERT INTO `creature_text` VALUES ('37846', '5', '0', 'But even Thalorien\'s skill and Quel\'Delar\'s magic could not save Silvermoon from the might of the Scourge.', '12', '0', '100', '1', '0', '16801', 'Blood Queen Lana\'thel - Say 5');
INSERT INTO `creature_text` VALUES ('37846', '6', '0', 'Thalorien fell before the gates of the Sunwell, buying time for others to escape.', '12', '0', '100', '1', '0', '16802', 'Blood Queen Lana\'thel - Say 6');
INSERT INTO `creature_text` VALUES ('37846', '4', '0', 'The king bestowed the blade upon my friend, Thalorien Dawnseeker.', '12', '0', '100', '1', '0', '16800', 'Blood Queen Lana\'thel - Say 4');
INSERT INTO `creature_text` VALUES ('37846', '3', '0', 'As Quel\'Serrar was forged by the dragons and given to the kaldorei, its twin,Quel\'Delar was given to my people.', '12', '0', '100', '1', '0', '16809', 'Blood Queen Lana\'thel - Say 3');
INSERT INTO `creature_text` VALUES ('37846', '2', '0', 'Quiet, fools. If I had wished to kill you, you\'d already be dead.', '12', '0', '100', '0', '0', '0', 'Blood Queen Lana\'thel - Say 2');
INSERT INTO `creature_text` VALUES ('36656', '1', '0', 'You are not welcome here, minion of the Lich King!', '12', '0', '100', '0', '0', '0', 'Silver Covenant Sentinel - Say 1');
