INSERT INTO `creature_text` VALUES ('36624', '1', '0', 'I knew this was a dragonforged blade when I first laid eyes on it.', '12', '0', '100', '1', '0', '16604', 'Caladis Brightspear - Say 1');
INSERT INTO `creature_text` VALUES ('36642', '3', '0', 'It falls to us to see Quel\'Delar restored for the Sunreavers.', '12', '0', '100', '0', '0', '0', 'Myralion Sunblaze - Say 3');
INSERT INTO `creature_text` VALUES ('36642', '2', '0', 'After all these years, can it really be --', '12', '0', '100', '6', '0', '16746', 'Myralion Sunblaze - Say 2');
INSERT INTO `creature_text` VALUES ('36642', '1', '0', 'The style of this blade and its markings hinted that it might be a dragonforged sword. At last, we will have our answer.', '12', '0', '100', '1', '0', '16745', 'Myralion Sunblaze - Say 1');
