INSERT INTO `creature_text` VALUES ('31003', '3', '0', 'We\'ve done it, lads! We\'ve taken the pinnacle from the Scourge! Report to Father Gustav at once and tell him the good news! We\'re gonna get to buildin\' and settin\' up! Go!', '14', '0', '100', '71', '0', '0', 'Crusader Lord Dalfors');
