INSERT INTO `creature_text` VALUES ('17876', '5', '0', 'Let\'s ride!', '12', '0', '100', '0', '0', '10469', 'thrall hillsbrad SAY_TH_MOUNTS_UP');
INSERT INTO `creature_text` VALUES ('17876', '4', '0', 'Very well. Tarren Mill lies just west of here. Since time is of the essence...', '14', '0', '100', '0', '0', '10468', 'thrall hillsbrad SAY_TH_START_EVENT_PART2');
INSERT INTO `creature_text` VALUES ('17876', '3', '0', 'I\'ll never be chained again!', '14', '0', '100', '0', '0', '10467', 'thrall hillsbrad SAY_TH_SKARLOC_TAUNT');
INSERT INTO `creature_text` VALUES ('17876', '2', '0', 'A rider approaches!', '12', '0', '100', '0', '0', '10466', 'thrall hillsbrad SAY_TH_SKARLOC_MEET');
INSERT INTO `creature_text` VALUES ('17876', '1', '0', 'As long as we\'re going with a new plan, I may aswell pick up a weapon and some armor.', '12', '0', '100', '0', '0', '0', 'thrall hillsbrad SAY_TH_ARMORY');
INSERT INTO `creature_text` VALUES ('17876', '0', '0', 'Very well then. Let\'s go!', '14', '0', '100', '0', '0', '10465', 'thrall hillsbrad SAY_TH_START_EVENT_PART1');
INSERT INTO `creature_text` VALUES ('17862', '3', '0', 'Thrall will never be free!', '14', '0', '100', '0', '0', '10409', 'skarloc SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17862', '3', '1', 'Did you really think you would leave here alive?', '14', '0', '100', '0', '0', '10410', 'skarloc SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('17862', '1', '0', 'You\'re a slave. That\'s all you\'ll ever be.', '14', '0', '100', '0', '0', '10407', 'skarloc SAY_TAUNT1');
INSERT INTO `creature_text` VALUES ('17862', '2', '0', 'I don\'t know what Blackmoore sees in you. For my money, you\'re just another ignorant savage!', '14', '0', '100', '0', '0', '10408', 'skarloc SAY_TAUNT2');
INSERT INTO `creature_text` VALUES ('17862', '0', '0', 'Thrall! You didn\'t really think you would escape did you? You and your allies shall answer to Blackmoore - after I\'ve had my fun!', '14', '0', '100', '0', '0', '10406', 'skarloc SAY_ENTER');
INSERT INTO `creature_text` VALUES ('18096', '3', '0', 'Not so fast!', '14', '0', '100', '0', '0', '10423', 'epoch hunter SAY_BREATH1');
INSERT INTO `creature_text` VALUES ('18096', '2', '1', 'Thrall will remain a slave. Taretha will die. You have failed.', '14', '0', '100', '0', '0', '10426', 'epoch hunter SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('18096', '1', '0', 'Enough! I will erase your very existence!', '14', '0', '100', '0', '0', '10421', 'epoch hunter SAY_AGGRO1');
INSERT INTO `creature_text` VALUES ('18096', '1', '1', 'You cannot fight fate!', '14', '0', '100', '0', '0', '10422', 'epoch hunter SAY_AGGRO2');
INSERT INTO `creature_text` VALUES ('18096', '2', '0', 'You are...irrelevant.', '14', '0', '100', '0', '0', '10425', 'epoch hunter SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('18096', '0', '2', 'Ah, there you are. I had hoped to accomplish this with a bit of subtlety, but I suppose direct confrontation was inevitable. Your future, Thrall, must not come to pass and so...you and your troublesome friends must die!', '14', '0', '100', '0', '0', '10420', 'epoch hunter SAY_ENTER3');
INSERT INTO `creature_text` VALUES ('18096', '0', '1', 'Taretha\'s life hangs in the balance. Surely you care for her. Surely you wish to save her...', '14', '0', '100', '0', '0', '10419', 'epoch hunter SAY_ENTER2');
INSERT INTO `creature_text` VALUES ('18096', '0', '0', 'Thrall! Come outside and face your fate!', '14', '0', '100', '0', '0', '10418', 'epoch hunter SAY_ENTER1');
INSERT INTO `creature_text` VALUES ('26529', '3', '0', 'This... not fun...', '14', '0', '100', '0', '0', '13433', 'meathook SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26529', '2', '0', 'New toys!', '14', '0', '100', '0', '0', '13429', 'meathook SAY_SPAWN');
INSERT INTO `creature_text` VALUES ('26529', '1', '2', 'Get up! Me not done!', '14', '0', '100', '0', '0', '13432', 'meathook SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26529', '1', '1', 'Why you stop moving?', '14', '0', '100', '0', '0', '13431', 'meathook SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26529', '1', '0', 'Boring...', '14', '0', '100', '0', '0', '13430', 'meathook SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26529', '0', '0', 'Play time!', '14', '0', '100', '0', '0', '13428', 'meathook SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26532', '2', '2', 'Let\'s get this over with. ', '14', '0', '100', '0', '0', '13412', 'epoch SAY_TIME_WARP_3');
INSERT INTO `creature_text` VALUES ('26532', '3', '0', 'There is no future for you.', '14', '0', '100', '0', '0', '13413', 'epoch SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26532', '3', '1', 'This is the hour of our greatest triumph!', '14', '0', '100', '0', '0', '13414', 'epoch SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26532', '2', '1', 'Not quick enough!', '14', '0', '100', '0', '0', '13411', 'epoch SAY_TIME_WARP_2');
INSERT INTO `creature_text` VALUES ('26532', '2', '0', 'Tick tock, tick tock...', '14', '0', '100', '0', '0', '13410', 'epoch SAY_TIME_WARP_1');
INSERT INTO `creature_text` VALUES ('26532', '1', '0', 'We\'ll see about that, young prince.', '14', '0', '100', '0', '0', '13409', 'epoch SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26532', '0', '0', 'Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own.', '14', '0', '100', '0', '0', '13408', 'epoch SAY_INTRO | culling SAY_PHASE314');
INSERT INTO `creature_text` VALUES ('26530', '5', '0', 'I want a sample...', '14', '0', '100', '0', '0', '13480', 'salramm SAY_STEAL_FLESH_1');
INSERT INTO `creature_text` VALUES ('26530', '5', '1', 'Such strength... it must be mine!', '14', '0', '100', '0', '0', '13481', 'salramm SAY_STEAL_FLESH_2');
INSERT INTO `creature_text` VALUES ('26530', '4', '1', 'Blood... destruction... EXHILARATING!', '14', '0', '100', '0', '0', '13479', 'salramm SAY_EXPLODE_GHOUL_2');
INSERT INTO `creature_text` VALUES ('26530', '4', '0', 'BOOM! Hahahahah...', '14', '0', '100', '0', '0', '13478', 'salramm SAY_EXPLODE_GHOUL_1');
INSERT INTO `creature_text` VALUES ('26530', '3', '0', 'You only advance... the master\'s plan...', '14', '0', '100', '0', '0', '13483', 'salramm SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26530', '2', '2', 'Don\'t worry, I\'ll make good use of you.', '14', '0', '100', '0', '0', '13475', 'salramm SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26530', '2', '1', 'Aah, quality materials!', '14', '0', '100', '0', '0', '13474', 'salramm SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26530', '2', '0', 'The fun is just beginning!', '14', '0', '100', '0', '0', '13473', 'salramm SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26530', '1', '0', 'You are too late, champion of Lordaeron. The dead shall have their day.', '14', '0', '100', '0', '0', '13471', 'salramm SAY_SPAWN');
INSERT INTO `creature_text` VALUES ('26530', '0', '0', 'Ah, the entertainment has arrived!', '14', '0', '100', '0', '0', '13472', 'salramm SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26528', '0', '0', 'Watch your tone with me, boy. You may be the prince, but I\'m still your superior as a paladin!', '12', '0', '100', '0', '0', '12839', 'culling SAY_PHASE102');
INSERT INTO `creature_text` VALUES ('26499', '38', '0', 'You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend.', '12', '0', '100', '0', '0', '14319', 'culling SAY_PHASE504');
INSERT INTO `creature_text` VALUES ('26499', '37', '0', 'I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth!', '12', '0', '100', '0', '0', '14318', 'culling SAY_PHASE503');
INSERT INTO `creature_text` VALUES ('26499', '36', '0', 'We\'re going to finish this right now, Mal\'Ganis. Just you... and me.', '12', '0', '100', '0', '0', '14317', 'culling SAY_PHASE502');
INSERT INTO `creature_text` VALUES ('26499', '35', '0', 'Justice will be done.', '12', '0', '100', '0', '0', '14316', 'culling SAY_PHASE501');
INSERT INTO `creature_text` VALUES ('26499', '34', '0', 'At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward.', '12', '0', '100', '0', '0', '14315', 'culling SAY_PHASE407');
INSERT INTO `creature_text` VALUES ('26499', '33', '0', 'That\'s enough; we must move again. Mal\'Ganis awaits.', '12', '0', '100', '0', '0', '14314', 'culling SAY_PHASE406');
INSERT INTO `creature_text` VALUES ('26499', '32', '0', 'Rest a moment and clear your lungs, but we must move again soon.', '12', '0', '100', '0', '0', '14313', 'culling SAY_PHASE405');
INSERT INTO `creature_text` VALUES ('26499', '30', '0', 'I\'m relieved this secret passage still works.', '12', '0', '100', '0', '0', '14311', 'culling SAY_PHASE403');
INSERT INTO `creature_text` VALUES ('26499', '31', '0', 'Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might.', '12', '0', '100', '0', '0', '14312', 'culling SAY_PHASE404');
INSERT INTO `creature_text` VALUES ('26499', '29', '0', 'This will only take a moment.', '12', '0', '100', '0', '0', '14310', 'culling SAY_PHASE402');
INSERT INTO `creature_text` VALUES ('26499', '28', '0', 'The quickest path to Mal\'Ganis lies behind that bookshelf ahead.', '12', '0', '100', '0', '0', '14308', 'culling SAY_PHASE401');
INSERT INTO `creature_text` VALUES ('26499', '25', '0', 'They\'re very persistent.', '12', '0', '100', '0', '0', '14306', 'culling SAY_PHASE312');
INSERT INTO `creature_text` VALUES ('26499', '26', '0', 'What else can he put in my way?', '12', '0', '100', '0', '0', '14307', 'culling SAY_PHASE313');
INSERT INTO `creature_text` VALUES ('26499', '27', '0', 'I do what I must for Lordaeron, and neither your words nor your actions will stop me.', '12', '0', '100', '0', '0', '14309', 'culling SAY_PHASE315');
INSERT INTO `creature_text` VALUES ('26499', '24', '0', 'Mal\'Ganis is not making this easy.', '12', '0', '100', '0', '0', '14305', 'culling SAY_PHASE311');
INSERT INTO `creature_text` VALUES ('26499', '23', '0', 'One less obstacle to deal with.', '12', '0', '100', '0', '0', '0', 'culling SAY_PHASE310. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES ('26499', '22', '0', 'Watch your backs: they have us surrounded in this hall.', '12', '0', '100', '0', '0', '14304', 'culling SAY_PHASE309');
INSERT INTO `creature_text` VALUES ('26499', '21', '0', 'Let\'s move on.', '12', '0', '100', '0', '0', '14303', 'culling SAY_PHASE308');
INSERT INTO `creature_text` VALUES ('26499', '20', '0', 'More vile sorcery! Be ready for anything!', '12', '0', '100', '0', '0', '14302', 'culling SAY_PHASE307');
INSERT INTO `creature_text` VALUES ('26499', '19', '0', 'Mal\'Ganis appears to have more than scourge in his arsenal. We should make haste.', '12', '0', '100', '0', '0', '14301', 'culling SAY_PHASE306. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES ('26499', '18', '0', 'What is this sorcery?', '12', '0', '100', '0', '0', '14300', 'culling SAY_PHASE304');
INSERT INTO `creature_text` VALUES ('26499', '17', '0', 'Yes, I\'m glad I could get to you before the plague.', '12', '0', '100', '0', '0', '14299', 'culling SAY_PHASE303');
INSERT INTO `creature_text` VALUES ('26499', '16', '0', 'Follow me, I know the way through.', '12', '0', '100', '0', '0', '14298', 'culling SAY_PHASE301');
INSERT INTO `creature_text` VALUES ('26499', '15', '0', 'Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis.', '14', '0', '100', '0', '0', '14297', 'culling SAY_PHASE210');
INSERT INTO `creature_text` VALUES ('26499', '14', '0', 'Mal\'ganis will send out some of his Scourge minions to interfere with us. Those of you with the strongest steel and magic shall go forth and destroy them. I will lead the rest of my forces in purging Stratholme of the infected.', '14', '0', '100', '0', '0', '14885', 'culling SAY_PHASE209');
INSERT INTO `creature_text` VALUES ('26499', '13', '0', 'I won\'t allow it, Mal\'Ganis! Better that these people die by my hand than serve as your slaves in death!', '14', '0', '100', '0', '0', '14296', 'culling SAY_PHASE208');
INSERT INTO `creature_text` VALUES ('26499', '12', '0', 'That was just the beginning.', '12', '0', '100', '0', '0', '14295', 'culling SAY_PHASE205');
INSERT INTO `creature_text` VALUES ('26499', '11', '0', 'I can only help you with a clean death.', '12', '0', '100', '0', '0', '14294', 'culling SAY_PHASE203');
INSERT INTO `creature_text` VALUES ('26499', '10', '0', 'Everyone looks ready. Remember, these people are all infected with the plague and will die soon. We must purge Stratholme to protect the remainder of Lordaeron from the Scourge. Let\'s go.', '12', '0', '100', '0', '0', '14293', 'culling SAY_PHASE201');
INSERT INTO `creature_text` VALUES ('26499', '8', '0', 'Jaina?', '12', '0', '100', '0', '0', '12836', 'culling SAY_PHASE116');
INSERT INTO `creature_text` VALUES ('26499', '9', '0', 'Take position here, and I will lead a small force inside Stratholme to begin the culling. We must contain and purge the infected for the sake of all of Lordaeron!', '14', '0', '100', '0', '0', '14327', 'culling SAY_PHASE118');
INSERT INTO `creature_text` VALUES ('26499', '7', '0', 'It\'s done! Those of you who have the will to save this land, follow me! The rest of you... get out of my sight!', '12', '0', '100', '0', '0', '12835', 'culling SAY_PHASE114');
INSERT INTO `creature_text` VALUES ('26499', '6', '0', 'Have I? Lord Uther, by my right of succession and the sovereignty of my crown, I hereby relieve you of your command and suspend your paladins from service.', '12', '0', '100', '0', '0', '12834', 'culling SAY_PHASE112');
INSERT INTO `creature_text` VALUES ('26499', '5', '0', 'Then I must consider this an act of treason.', '12', '0', '100', '0', '0', '12833', 'culling SAY_PHASE110');
INSERT INTO `creature_text` VALUES ('26499', '3', '0', 'This entire city must be purged.', '12', '0', '100', '0', '0', '12831', 'culling SAY_PHASE106');
INSERT INTO `creature_text` VALUES ('26499', '4', '0', 'Damn it, Uther! As your future king, I order you to purge this city!', '12', '0', '100', '0', '0', '12832', 'culling SAY_PHASE108');
INSERT INTO `creature_text` VALUES ('26499', '2', '0', 'Oh, no. We\'re too late. These people have all been infected! They may look fine now, but it\'s just a matter of time before they turn into the undead!', '12', '0', '100', '0', '0', '12830', 'culling SAY_PHASE104');
INSERT INTO `creature_text` VALUES ('26499', '1', '0', 'As if I could forget. Listen, Uther, there\'s something about the plague you should know...', '12', '0', '100', '0', '0', '12829', 'culling SAY_PHASE103');
INSERT INTO `creature_text` VALUES ('26499', '0', '0', 'Glad you could make it, Uther.', '12', '0', '100', '0', '0', '12828', 'culling SAY_PHASE101');
INSERT INTO `creature_text` VALUES ('26533', '9', '0', 'You\'ll never defeat the Lich King without my forces! I\'ll have my revenge...on him, AND you...', '14', '0', '100', '0', '0', '14429', 'malganis SAY_ESCAPE_SPEECH_2');
INSERT INTO `creature_text` VALUES ('26533', '7', '0', '(Eredun) I AM MAL\'GANIS! I AM ETERNAL!', '14', '0', '100', '0', '0', '14427', 'malganis SAY_15HEALTH');
INSERT INTO `creature_text` VALUES ('26533', '8', '0', 'ENOUGH! I waste my time here...I must gather my strength on the home world...', '14', '0', '100', '0', '0', '14428', 'malganis SAY_ESCAPE_SPEECH_1');
INSERT INTO `creature_text` VALUES ('26533', '6', '0', 'I spent too much time in that weak little shell...', '14', '0', '100', '0', '0', '14426', 'malganis SAY_30HEALTH');
INSERT INTO `creature_text` VALUES ('26533', '5', '1', 'You seem...tired...', '14', '0', '100', '0', '0', '14415', 'malganis SAY_SLEEP_2');
INSERT INTO `creature_text` VALUES ('26533', '5', '0', 'Time out...', '14', '0', '100', '0', '0', '14414', 'malganis SAY_SLEEP_1');
INSERT INTO `creature_text` VALUES ('26533', '4', '3', 'Your time has come to an end!', '14', '0', '100', '0', '0', '14425', 'malganis SAY_SLAY_4');
INSERT INTO `creature_text` VALUES ('26533', '4', '2', 'Your death is in vain, tiny mortal...', '14', '0', '100', '0', '0', '14424', 'malganis SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26533', '4', '1', 'My onslaught will wash over the Lich King\'s forces...', '14', '0', '100', '0', '0', '14423', 'malganis SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26533', '4', '0', 'Anak\'Keri...', '14', '0', '100', '0', '0', '14422', 'malganis SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26533', '3', '2', 'It is Prince Arthas I want... not you...', '14', '0', '100', '0', '0', '14418', 'malganis SAY_KILL_3');
INSERT INTO `creature_text` VALUES ('26533', '3', '1', 'The dark lord is displeased with your interference...', '14', '0', '100', '0', '0', '14417', 'malganis SAY_KILL_2');
INSERT INTO `creature_text` VALUES ('26533', '3', '0', 'All too easy...', '14', '0', '100', '0', '0', '14416', 'malganis SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('26533', '2', '0', 'This will be a fine test...Prince Arthas...', '14', '0', '100', '0', '0', '14413', 'malganis SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26533', '0', '0', 'Yes, this is the beginning. I\'ve been waiting for you, young prince. I am Mal\'Ganis.', '14', '0', '100', '0', '0', '14410', 'malganis SAY_INTRO_1 | culling SAY_PHASE206');
INSERT INTO `creature_text` VALUES ('26533', '1', '0', 'As you can see, your people are now mine. I will now turn this city household by household, until the flame of life has been snuffed out... forever.', '14', '0', '100', '0', '0', '14411', 'malganis SAY_INTRO_2 | culling SAY_PHASE207');
INSERT INTO `creature_text` VALUES ('17879', '3', '0', 'I told you it was a fool\'s quest!', '14', '0', '100', '0', '0', '10415', 'chrono lord deja SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17879', '3', '1', 'Leaving so soon?', '14', '0', '100', '0', '0', '10416', 'chrono lord deja SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('17879', '2', '0', 'You have outstayed your welcome, Timekeeper. Begone!', '14', '0', '100', '0', '0', '10413', 'chrono lord deja SAY_BANISH');
INSERT INTO `creature_text` VALUES ('17879', '1', '0', 'If you will not cease this foolish quest, then you will die!', '14', '0', '100', '0', '0', '10414', 'chrono lord deja SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17879', '0', '0', 'Why do you aid the Magus? Just think of how many lives could be saved if the portal is never opened, if the resulting wars could be erased ...', '14', '0', '100', '0', '0', '10412', 'chrono lord deja SAY_ENTER');
INSERT INTO `creature_text` VALUES ('17881', '2', '0', 'Your time is up, slave of the past!', '14', '0', '100', '0', '0', '10401', 'aeonus SAY_BANISH');
INSERT INTO `creature_text` VALUES ('17881', '3', '0', 'One less obstacle in our way!', '14', '0', '100', '0', '0', '10403', 'aeonus SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17881', '3', '1', 'No one can stop us! No one!', '14', '0', '100', '0', '0', '10404', 'aeonus SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('17881', '1', '0', 'Let us see what fate lays in store...', '14', '0', '100', '0', '0', '10402', 'aeonus SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17881', '0', '0', 'The time has come to shatter this clockwork universe forever! Let us no longer be slaves of the hourglass! I warn you: those who do not embrace the greater path shall become victims of its passing!', '14', '0', '100', '0', '0', '10400', 'aeonus SAY_ENTER');
INSERT INTO `creature_text` VALUES ('15608', '7', '0', 'Orcs of the Horde! This portal is the gateway to your new destiny! Azeroth lies before you, ripe for the taking!', '14', '0', '100', '0', '0', '0', 'medivh SAY_ORCS_ENTER');
INSERT INTO `creature_text` VALUES ('15608', '6', '0', 'I am grateful for your aid, champions. Now, Gul\'dan\'s Horde will sweep across this world like a locust swarm, and all my designs, all my carefully-laid plans will at last fall into place.', '14', '0', '100', '0', '0', '10440', 'medivh SAY_WIN');
INSERT INTO `creature_text` VALUES ('15608', '5', '0', 'No! Damn this feeble, mortal coil!', '14', '0', '100', '0', '0', '10441', 'medivh SAY_DEATH');
INSERT INTO `creature_text` VALUES ('15608', '4', '0', 'The shield is nearly gone! All that I have worked for is in danger!', '14', '0', '100', '0', '0', '10439', 'medivh SAY_WEAK25');
INSERT INTO `creature_text` VALUES ('15608', '3', '0', 'My powers must be concentrated on the portal! I do not have time to hold the shield!', '14', '0', '100', '0', '0', '10438', 'medivh SAY_WEAK50');
INSERT INTO `creature_text` VALUES ('15608', '2', '0', 'Champions! My shield grows weak!', '14', '0', '100', '0', '0', '10437', 'medivh SAY_WEAK75');
INSERT INTO `creature_text` VALUES ('15608', '1', '0', 'What is this? Champions, coming to my aid? I sense the hand of the dark one in this. Truly this sacred event bears his blessing?', '14', '0', '100', '0', '0', '10436', 'medivh SAY_INTRO');
INSERT INTO `creature_text` VALUES ('15608', '0', '0', 'The time has come! Gul\'dan, order your warlocks to double their efforts! Moments from now the gateway will open and your Horde will be released upon this ripe, unsuspecting world!', '14', '0', '100', '0', '0', '10435', 'medivh SAY_ENTER');
INSERT INTO `creature_text` VALUES ('17880', '3', '0', 'You should have left when you had the chance.', '14', '0', '100', '0', '0', '10445', 'temporus SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17880', '2', '0', 'Time... sands of time is run out for you.', '14', '0', '100', '0', '0', '10443', 'temporus SAY_BANISH');
INSERT INTO `creature_text` VALUES ('17880', '1', '0', 'So be it ... you have been warned.', '14', '0', '100', '0', '0', '10444', 'temporus SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17880', '0', '0', 'Why do you persist? Surely you can see the futility of it all. It is not too late! You may still leave with your lives ...', '14', '0', '100', '0', '0', '10442', 'temporus SAY_ENTER');
INSERT INTO `creature_text` VALUES ('23002', '3', '0', '%s takes the Southfury moonstone and escapes into the river. Follow her!', '41', '0', '100', '0', '0', '0', 'sprysprocket EMOTE_START');
INSERT INTO `creature_text` VALUES ('23002', '2', '0', 'All right, you win! I surrender! Just don\'t hurt me!', '14', '0', '100', '0', '0', '0', 'sprysprocket SAY_END');
INSERT INTO `creature_text` VALUES ('23002', '1', '0', 'Just chill!', '15', '0', '100', '0', '0', '0', 'sprysprocket SAY_WHISPER_CHILL');
INSERT INTO `creature_text` VALUES ('23002', '0', '0', 'You, there! Hand over that moonstone and nobody gets hurt!', '14', '0', '100', '0', '0', '0', 'sprysprocket SAY_START');
INSERT INTO `creature_text` VALUES ('3568', '1', '0', '%s growls in acknowledgement before straightening and making her way off into the forest.', '16', '0', '100', '0', '0', '0', 'mist EMOTE_AT_HOME');
INSERT INTO `creature_text` VALUES ('3568', '0', '0', 'Mist! I feared I would never see you again! Yes, I am well, do not worry for me. You must rest and recover your health.', '12', '0', '100', '0', '0', '0', 'mist SAY_AT_HOME');
INSERT INTO `creature_text` VALUES ('15380', '1', '0', 'Let them feel the wrath of the Blue Flight! May Malygos protect me!', '14', '0', '100', '0', '0', '0', 'anachronos the ancient ARYGOS_YELL_1');
INSERT INTO `creature_text` VALUES ('15380', '0', '0', 'This distraction will give you and the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ARYGOS_SAY_1');
INSERT INTO `creature_text` VALUES ('15379', '2', '0', 'Alexstrasza grant me the resolve to drive our enemies back!', '14', '0', '100', '0', '0', '0', 'anachronos the ancient CAELESTRASZ_YELL_1');
INSERT INTO `creature_text` VALUES ('15379', '1', '0', 'Do not forget the sacrifices made on this day, night elf. We have all suffered immensely at the hands of these beasts.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient CAELESTRASZ_SAY_2');
INSERT INTO `creature_text` VALUES ('15382', '6', '0', 'falls to one knee - exhausted.', '16', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_EMOTE_1');
INSERT INTO `creature_text` VALUES ('15382', '7', '0', 'hurls the Scepter of the Shifting Sands into the barrier, shattering it.', '16', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_EMOTE_2');
INSERT INTO `creature_text` VALUES ('15379', '0', '0', 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient CAELESTRASZ_SAY_1');
INSERT INTO `creature_text` VALUES ('15382', '5', '0', 'My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back.Thought it takes a millennia, I WILL have my son back!', '12', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_6');
INSERT INTO `creature_text` VALUES ('15382', '4', '0', 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', '12', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_5');
INSERT INTO `creature_text` VALUES ('15382', '3', '0', 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_4');
INSERT INTO `creature_text` VALUES ('15382', '2', '0', 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', '12', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_3');
INSERT INTO `creature_text` VALUES ('15382', '1', '0', 'It is done dragon. Lead the way...', '12', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_2');
INSERT INTO `creature_text` VALUES ('15382', '0', '0', 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_1');
INSERT INTO `creature_text` VALUES ('15381', '12', '0', 'kneels down to pick up the fragments of the shattered scepter.', '16', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_EMOTE_3');
INSERT INTO `creature_text` VALUES ('15381', '10', '0', 'hands the Scepter of the Shifting Sands to Fandral Staghelm.', '16', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_EMOTE_1');
INSERT INTO `creature_text` VALUES ('15381', '11', '0', 'shakes his head in dissapointment.', '16', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_EMOTE_2');
INSERT INTO `creature_text` VALUES ('15381', '0', '0', 'We must act quickly or all shall be lost!', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_1');
INSERT INTO `creature_text` VALUES ('15381', '1', '0', 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_2');
INSERT INTO `creature_text` VALUES ('15381', '2', '0', 'Stay close...', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_3');
INSERT INTO `creature_text` VALUES ('15381', '3', '0', 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_4');
INSERT INTO `creature_text` VALUES ('15381', '4', '0', 'FINISH THE SPELL STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_5');
INSERT INTO `creature_text` VALUES ('15381', '5', '0', 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_6');
INSERT INTO `creature_text` VALUES ('15381', '6', '0', 'There is but one duty that remains...', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_7');
INSERT INTO `creature_text` VALUES ('15381', '7', '0', 'Before I leave this place, I make one final offreing to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn\'Qiraj will be unleashed upon the world once more.', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_8');
INSERT INTO `creature_text` VALUES ('15381', '8', '0', 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_9');
INSERT INTO `creature_text` VALUES ('15381', '9', '0', 'And now you know all that there is to know, mortal...', '12', '0', '100', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_10');
INSERT INTO `creature_text` VALUES ('33515', '1', '0', 'The secret dies with you!', '14', '0', '100', '0', '0', '15474', 'Auriaya SAY_SLAY_1');
