INSERT INTO `creature_text` VALUES ('32913', '0', '0', 'Mortals have no place here!', '14', '0', '100', '0', '0', '15493', 'Elder Ironbranch SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('32915', '2', '0', 'Matron, one has fallen!', '14', '0', '100', '0', '0', '15487', 'Elder Brightleaf SAY_DEATH');
INSERT INTO `creature_text` VALUES ('33293', '4', '0', 'I... I think I broke it.', '14', '0', '100', '0', '0', '15728', 'XT002 SAY_SLAY_1');
