INSERT INTO `creature_text` VALUES ('33271', '1', '1', 'Defiance... a flaw of mortality.', '14', '0', '100', '0', '0', '15544', 'General Vezax SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('32927', '5', '0', 'This meeting of the Assembly of Iron is adjourned!', '14', '0', '100', '0', '0', '15664', 'Molgeim SAY_BERSERK');
