INSERT INTO `creature_text` VALUES ('32930', '2', '0', 'Just a scratch!', '14', '0', '100', '0', '0', '15589', 'Kologarn SAY_LEFT_ARM_GONE');
INSERT INTO `creature_text` VALUES ('33210', '0', '0', 'Welcome, champions! All of our attempts at grounding her have failed. We could use a hand in bring her down with these harpoon guns.', '14', '0', '100', '0', '0', '15647', 'Exp Commander SAY_GREET');
INSERT INTO `creature_text` VALUES ('33186', '1', '0', 'Razorscale grounded permanently!', '41', '0', '100', '0', '0', '0', 'Razorscale EMOTE_PERMA');
INSERT INTO `creature_text` VALUES ('33186', '0', '0', 'Razorscale takes a deep breath...', '41', '0', '100', '0', '0', '0', 'Razorscale EMOTE_BREATH');
INSERT INTO `creature_text` VALUES ('32913', '1', '0', 'I return you whence you came!', '14', '0', '100', '0', '0', '15494', 'Elder Ironbranch SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('32845', '1', '1', 'Welcome to the endless winter.', '14', '0', '100', '0', '0', '15554', 'Hodir SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('32845', '2', '0', 'Winds of the north consume you!', '14', '0', '100', '0', '0', '15555', 'Hodir SAY_FLASH_FREEZE');
INSERT INTO `creature_text` VALUES ('32845', '3', '0', 'Hodir roars furious.', '41', '0', '100', '0', '0', '15556', 'Hodir SAY_STALACTITE');
INSERT INTO `creature_text` VALUES ('32845', '4', '0', 'I... I am released from his grasp... at last.', '14', '0', '100', '0', '0', '15557', 'Hodir SAY_DEATH');
INSERT INTO `creature_text` VALUES ('33118', '1', '1', 'Your bones will serve as kindling!', '14', '0', '100', '0', '0', '15570', 'Ignis SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('33118', '2', '0', 'I. Have. Failed.', '14', '0', '100', '0', '0', '15572', 'Ignis SAY_DEATH');
INSERT INTO `creature_text` VALUES ('33118', '3', '0', 'Arise, soldiers of the Iron Crucible! The Makers\' will be done!', '14', '0', '100', '0', '0', '15565', 'Ignis SAY_SUMMON');
INSERT INTO `creature_text` VALUES ('33118', '4', '0', 'I will burn away your impurities!', '14', '0', '100', '0', '0', '15566', 'Ignis SAY_SLAG_POT');
INSERT INTO `creature_text` VALUES ('32906', '0', '0', 'The Conservatory must be protected!', '14', '0', '100', '0', '0', '15526', 'Freya SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('32906', '1', '0', 'Elders, grant me your strength!', '14', '0', '100', '0', '0', '15527', 'Freya SAY_AGGRO_WITH_ELDER');
INSERT INTO `creature_text` VALUES ('32906', '2', '0', 'Forgive me.', '14', '0', '100', '0', '0', '15529', 'Freya SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('32906', '2', '1', 'From your death springs life anew!', '14', '0', '100', '0', '0', '15530', 'Freya SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('32906', '3', '0', 'His hold on me dissipates. I can see clearly once more. Thank you, heroes.', '14', '0', '100', '0', '0', '15531', 'Freya SAY_DEATH');
INSERT INTO `creature_text` VALUES ('32906', '4', '0', 'You have strayed too far, wasted too much time!', '14', '0', '100', '0', '0', '15532', 'Freya SAY_BERSERK');
INSERT INTO `creature_text` VALUES ('32865', '8', '2', 'I need time to reflect.... I will aid your cause if you should require it. I owe you at least that much. Farewell.', '14', '0', '100', '0', '0', '15746', 'Thorim SAY_END_NORMAL_3');
INSERT INTO `creature_text` VALUES ('32865', '8', '1', 'Sif... was Sif here? Impossible--she died by my brother\'s hand. A dark nightmare indeed....', '14', '0', '100', '0', '0', '15745', 'Thorim SAY_END_NORMAL_2');
INSERT INTO `creature_text` VALUES ('32865', '8', '0', 'I feel as though I am awakening from a nightmare, but the shadows in this place yet linger.', '14', '0', '100', '0', '0', '15744', 'Thorim SAY_END_NORMAL_1');
INSERT INTO `creature_text` VALUES ('32865', '7', '0', 'Stay your arms! I yield!', '14', '0', '100', '0', '0', '15743', 'Thorim SAY_DEATH');
INSERT INTO `creature_text` VALUES ('32865', '6', '0', 'Failures! Weaklings!', '14', '0', '100', '0', '0', '15742', 'Thorim SAY_WIPE');
INSERT INTO `creature_text` VALUES ('32865', '4', '1', 'Pathetic!', '14', '0', '100', '0', '0', '15740', 'Thorim SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('32865', '5', '0', 'My patience has reached its limit!', '14', '0', '100', '0', '0', '15741', 'Thorim SAY_BERSERK');
INSERT INTO `creature_text` VALUES ('32865', '4', '0', 'Can\'t you at least put up a fight!?', '14', '0', '100', '0', '0', '15739', 'Thorim SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('16483', '1', '2', 'Ughhh... I hurt. Can you help me?', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HELP3');
INSERT INTO `creature_text` VALUES ('16483', '1', '3', 'I don\'t know if I can make it, please help me...', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HELP4');
INSERT INTO `creature_text` VALUES ('16483', '1', '1', 'Everything hurts, Please make it stop...', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HELP2');
INSERT INTO `creature_text` VALUES ('16483', '1', '0', 'Oh, the pain...', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HELP1');
INSERT INTO `creature_text` VALUES ('16483', '0', '3', 'Ugh... what is this place? Is that all that\'s left of the ship over there?', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HEAL4');
INSERT INTO `creature_text` VALUES ('16483', '0', '2', '$C You saved me! I owe you a debt that I can never repay. I\'ll go see if I can help the others.', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HEAL3');
INSERT INTO `creature_text` VALUES ('16483', '0', '1', '$C, Where am I? Who are you? Oh no! What happened to the ship?', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HEAL2');
INSERT INTO `creature_text` VALUES ('16483', '0', '0', 'The last thing I remember is the ship falling and us getting into the pods. I\'ll go see how I can help. Thank you!', '12', '0', '100', '0', '0', '0', 'draenei survivor SAY_HEAL1');
INSERT INTO `creature_text` VALUES ('11218', '3', '1', 'It\'s quiet... Too quiet...', '12', '0', '100', '0', '0', '0', 'kerlonian SAY_KER_ALERT_2');
INSERT INTO `creature_text` VALUES ('17243', '0', '0', 'Yes Master, all goes along as planned.', '12', '0', '100', '0', '0', '0', 'engineer spark SAY_TEXT');
INSERT INTO `creature_text` VALUES ('17312', '5', '0', '%s hugs her father.', '16', '0', '100', '0', '0', '0', 'magwin EMOTE_HUG');
INSERT INTO `creature_text` VALUES ('17312', '0', '0', 'Our house is this way, through the thicket.', '12', '0', '100', '0', '0', '0', 'magwin SAY_START');
INSERT INTO `creature_text` VALUES ('17312', '1', '0', 'Help me!', '12', '0', '100', '0', '0', '0', 'magwin SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17312', '2', '0', 'My poor family. Everything has been destroyed.', '12', '0', '100', '0', '0', '0', 'magwin SAY_PROGRESS');
INSERT INTO `creature_text` VALUES ('17312', '3', '0', 'Father! Father! You\'re alive!', '12', '0', '100', '0', '0', '0', 'magwin SAY_END1');
INSERT INTO `creature_text` VALUES ('17312', '4', '0', 'You can thank $N for getting me back here safely, father.', '12', '0', '100', '0', '0', '0', 'magwin SAY_END2');
INSERT INTO `creature_text` VALUES ('17243', '5', '0', 'Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool.', '12', '0', '100', '0', '0', '0', 'geezle SPARK_SAY_5');
INSERT INTO `creature_text` VALUES ('17243', '4', '0', 'The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside.', '12', '0', '100', '0', '0', '0', 'geezle SPARK_SAY_3');
INSERT INTO `creature_text` VALUES ('17318', '1', '0', 'Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process.', '12', '0', '100', '0', '0', '0', 'geezle GEEZLE_SAY_4');
INSERT INTO `creature_text` VALUES ('17318', '0', '0', 'What\'s the big idea, Spark?', '12', '0', '100', '0', '0', '0', 'geezle GEEZLE_SAY_1');
INSERT INTO `creature_text` VALUES ('17375', '0', '2', '[Fulborg] Those remaining at Stillpine Hold will welcome you as a hero!', '12', '0', '100', '0', '0', '0', 'stillpine capitive CAPITIVE_SAY_3');
INSERT INTO `creature_text` VALUES ('17375', '0', '1', '[Fulborg] Thank you, $N', '12', '0', '100', '0', '0', '0', 'stillpine capitive CAPITIVE_SAY_2');
INSERT INTO `creature_text` VALUES ('17375', '0', '0', '[Fulborg] The Stillpine furbolgs will not soon forget your bravery!', '12', '0', '100', '0', '0', '0', 'stillpine capitive CAPITIVE_SAY_1');
INSERT INTO `creature_text` VALUES ('6729', '1', '0', 'Speak with me to hear my tale.', '12', '0', '100', '0', '0', '0', 'morridune SAY_MORRIDUNE_2');
INSERT INTO `creature_text` VALUES ('6729', '0', '0', 'Aku\'mai is dead! At last, I can leave this wretched place.', '14', '0', '100', '0', '0', '0', 'morridune SAY_MORRIDUNE_1');
