INSERT INTO `creature_text` VALUES ('11218', '0', '0', 'Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $N.', '12', '0', '100', '0', '0', '0', 'kerlonian SAY_KER_START');
INSERT INTO `creature_text` VALUES ('2917', '10', '0', 'Ah. I remember now! I gave the mysterious fossil to Hollee! Check with her.', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_REMEMBER');
INSERT INTO `creature_text` VALUES ('2917', '9', '0', 'Nope. didn\'t leave the fossil back here!', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_PROGRESS');
INSERT INTO `creature_text` VALUES ('2917', '8', '0', 'Oh wait! I\'m supposed to be looking for that mysterious fossil!', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_MOSS_PROGRESS');
INSERT INTO `creature_text` VALUES ('2917', '7', '0', '%s inspects the ancient, mossy stone.', '16', '0', '100', '0', '0', '0', 'remtravel EMOTE_REM_MOSS');
INSERT INTO `creature_text` VALUES ('2917', '6', '0', 'Interesting... I hadn\'t noticed this earlier...', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_MOSS');
INSERT INTO `creature_text` VALUES ('2917', '5', '0', 'Oh wait, that\'s Hollee\'s tent... and it\'s empty.', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_TENT1_2');
INSERT INTO `creature_text` VALUES ('2917', '4', '0', 'I bet you I left it in the tent!', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_TENT1_1');
INSERT INTO `creature_text` VALUES ('2917', '3', '0', 'No mysterious fossil here... Ah, but my copy of Green Hills of Stranglethorn. What a good book!', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_BOOK');
INSERT INTO `creature_text` VALUES ('2917', '2', '0', 'Hrm, nothing up here.', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_RAMP1_2');
INSERT INTO `creature_text` VALUES ('2917', '1', '0', 'Now where did I put that mysterious fossil? Ah, maybe up there...', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_RAMP1_1');
INSERT INTO `creature_text` VALUES ('2917', '0', '0', 'Ok, $n, let\'s go find where I left that mysterious fossil. Follow me!', '12', '0', '100', '0', '0', '0', 'remtravel SAY_REM_START');
INSERT INTO `creature_text` VALUES ('4966', '3', '0', 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', '12', '0', '100', '0', '0', '0', 'hendel SAY_PROGRESS_4_TER');
INSERT INTO `creature_text` VALUES ('4966', '2', '0', 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', '12', '0', '100', '0', '0', '0', 'hendel SAY_PROGRESS_3_TER');
INSERT INTO `creature_text` VALUES ('4966', '1', '0', 'Please... please... Miss Proudmore. I didn\'t mean to...', '12', '0', '100', '0', '0', '0', 'hendel SAY_PROGRESS_2_HEN');
INSERT INTO `creature_text` VALUES ('4966', '0', '0', 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak for you back in the tower.', '12', '0', '100', '0', '0', '0', 'hendel SAY_PROGRESS_1_TER');
INSERT INTO `creature_text` VALUES ('23864', '1', '0', 'All who venture here belong to me,including you!', '14', '0', '100', '0', '0', '0', 'zelfrax SAY_ZELFRAX2');
INSERT INTO `creature_text` VALUES ('23864', '0', '0', 'This land was mine long before your wretched kind set foot here.', '14', '0', '100', '0', '0', '0', 'zelfrax SAY_ZELFRAX1');
