INSERT INTO `creature_text` VALUES ('4508', '8', '0', 'Finally! I am glad that I come, finally out here.', '12', '0', '100', '0', '0', '0', 'willix SAY_FINALY');
INSERT INTO `creature_text` VALUES ('4508', '7', '0', 'I am glad that we are out again from this damned ditch. However, up here it is not much better!', '12', '0', '100', '0', '0', '0', 'willix SAY_SOON');
INSERT INTO `creature_text` VALUES ('4508', '6', '0', 'I think, I see a way how we come out of this damned thorn tangle.', '12', '0', '100', '0', '0', '0', 'willix SAY_THINK');
INSERT INTO `creature_text` VALUES ('4508', '5', '0', 'I do not understand how these disgusting animals can live at such a place.... puh as this stinks!', '12', '0', '100', '0', '0', '0', 'willix SAY_BAD');
