INSERT INTO `creature_text` VALUES ('4508', '3', '0', 'In this ditch there are Blueleaf Tuber! As if the gold waited only to be dug out, I say it you!', '12', '0', '100', '0', '0', '0', 'willix SAY_BLUELEAF');
INSERT INTO `creature_text` VALUES ('4508', '2', '0', 'Help! Get this Raging Agam\'ar from me!', '12', '0', '100', '0', '0', '0', 'willix SAY_AGGRO1');
INSERT INTO `creature_text` VALUES ('4508', '1', '0', 'There on top resides Charlga Razorflank. The damned old Crone.', '12', '0', '100', '0', '0', '0', 'willix SAY_POINT');
INSERT INTO `creature_text` VALUES ('4508', '0', '0', 'Phew! Finally, out here. However, it will not become easy. Detain your eyes after annoyance.', '12', '0', '100', '0', '0', '0', 'willix SAY_READY');
