INSERT INTO `creature_text` VALUES ('9453', '0', '0', 'That kodo sure is a beauty. Wait a minute, where are my bifocals? Perhaps you should inspect the beast for me.', '12', '0', '100', '0', '0', '0', 'kodo round SAY_SMEED_HOME_3');
