INSERT INTO `creature_text` VALUES ('6015', '1', '0', 'Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub.', '12', '0', '100', '0', '0', '0', 'tooga SAY_TORT_POST_4');
INSERT INTO `creature_text` VALUES ('5955', '2', '0', 'My dearest Torta. I have been gone for so long. Finally we are reunited. At long last our love can blossom again.', '12', '0', '100', '0', '0', '0', 'tooga SAY_TOOG_POST_3');
INSERT INTO `creature_text` VALUES ('5955', '0', '1', 'Torta must be so worried.', '12', '0', '100', '0', '0', '0', 'tooga SAY_TOOG_WORRIED');
INSERT INTO `creature_text` VALUES ('5955', '1', '0', 'Torta, my love! I have returned at long last.', '12', '0', '100', '0', '0', '0', 'tooga SAY_TOOG_POST_1');
INSERT INTO `creature_text` VALUES ('6015', '0', '0', 'You have any idea how long I\'ve been waiting here? And where\'s dinner? All that time gone and nothing to show for it?', '12', '0', '100', '0', '0', '0', 'tooga SAY_TORT_POST_2');
INSERT INTO `creature_text` VALUES ('5955', '0', '0', 'I\'m Thirsty.', '12', '0', '100', '0', '0', '0', 'tooga SAY_TOOG_THIRST');
INSERT INTO `creature_text` VALUES ('15727', '0', '0', '%s is weakened!', '16', '0', '100', '0', '0', '0', 'cthun EMOTE_WEAKENED');
