INSERT INTO `creature_text` VALUES ('15509', '0', '0', '%s goes into a killing frenzy!', '16', '0', '100', '0', '0', '0', 'huhuran EMOTE_FRENZY_KILL');
INSERT INTO `creature_text` VALUES ('15516', '1', '0', 'I sentence you to death!', '14', '0', '100', '0', '0', '8647', 'sartura SAY_SLAY');
INSERT INTO `creature_text` VALUES ('15516', '0', '0', 'You will be judged for defiling these sacred grounds! The laws of the Ancients will not be challenged! Trespassers will be annihilated!', '14', '0', '100', '0', '0', '8646', 'sartura SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('15263', '1', '2', 'The screams of the dying will fill the air. A symphony of terror is about to begin!', '14', '0', '100', '0', '0', '8620', 'skeram SAY_SLAY3');
INSERT INTO `creature_text` VALUES ('15263', '1', '1', 'Spineless wretches! You will drown in rivers of blood!', '14', '0', '100', '0', '0', '8619', 'skeram SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('15263', '1', '0', 'Let your death serve as an example!', '14', '0', '100', '0', '0', '8617', 'skeram SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('15263', '0', '2', 'Tremble! The end is upon you.', '14', '0', '100', '0', '0', '8621', 'skeram SAY_AGGRO3');
INSERT INTO `creature_text` VALUES ('15263', '0', '1', 'Cower mortals! The age of darkness is at hand.', '14', '0', '100', '0', '0', '8616', 'skeram SAY_AGGRO2');
INSERT INTO `creature_text` VALUES ('15263', '0', '0', 'Are you so eager to die? I will be happy to accommodate you...', '14', '0', '100', '0', '0', '8615', 'skeram SAY_AGGRO1');
INSERT INTO `creature_text` VALUES ('10638', '0', '0', 'Here they come! Defend yourself!', '12', '0', '100', '0', '0', '0', 'kanati SAY_KAN_START');
INSERT INTO `creature_text` VALUES ('10646', '3', '0', 'More Grimtotems are coming this way!', '12', '0', '100', '0', '0', '0', 'lakota SAY_LAKO_MORE');
INSERT INTO `creature_text` VALUES ('10646', '4', '0', 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', '12', '0', '100', '0', '0', '0', 'lakota SAY_LAKO_END');
INSERT INTO `creature_text` VALUES ('10646', '2', '0', 'Here they come.', '12', '0', '100', '0', '0', '0', 'lakota SAY_LAKO_HERE_COME');
INSERT INTO `creature_text` VALUES ('10646', '1', '0', 'Look out, the Grimtotem are upon us!', '12', '0', '100', '0', '0', '0', 'lakota SAY_LAKO_LOOK_OUT');
INSERT INTO `creature_text` VALUES ('10646', '0', '0', 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', '12', '0', '100', '0', '0', '0', 'lakota SAY_LAKO_START');
INSERT INTO `creature_text` VALUES ('10427', '2', '0', 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', '12', '0', '100', '0', '0', '0', 'paoka SAY_COMPLETE');
INSERT INTO `creature_text` VALUES ('10427', '0', '0', 'Let\'s go $N. I am ready to reach Whitereach Post.', '12', '0', '100', '0', '0', '0', 'paoka SAY_START');
INSERT INTO `creature_text` VALUES ('10427', '1', '0', 'Now this looks familiar. If we keep heading east, I think we can... Ahh, Wyvern on the attack!', '12', '0', '100', '0', '0', '0', 'paoka SAY_WYVERN');
INSERT INTO `creature_text` VALUES ('3679', '4', '0', 'Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions.', '12', '0', '100', '0', '0', '0', 'naralex SAY_THANK_YOU');
INSERT INTO `creature_text` VALUES ('3679', '3', '0', 'I AM AWAKE, AT LAST!', '14', '0', '100', '0', '0', '0', 'naralex SAY_I_AM_AWAKE');
INSERT INTO `creature_text` VALUES ('3678', '10', '0', 'At last! Naralex awakes from the nightmare.', '12', '0', '100', '0', '0', '0', 'disciple SAY_NARALEX_AWAKES');
INSERT INTO `creature_text` VALUES ('3678', '9', '0', 'This Mutanus the Devourer is a minion from Naralex\'s nightmare no doubt!', '12', '0', '100', '0', '0', '0', 'disciple SAY_MUTANUS_THE_DEVOURER');
INSERT INTO `creature_text` VALUES ('3679', '2', '0', '%s dreams up a horrendous vision. Something stirs beneath the murky waters.', '16', '0', '100', '0', '0', '0', 'naralex EMOTE_HORRENDOUS_VISION');
INSERT INTO `creature_text` VALUES ('3679', '1', '0', '%s writhes in agony. The Disciple seems to be breaking through.', '16', '0', '100', '0', '0', '0', 'naralex EMOTE_WRITHE_IN_AGONY');
INSERT INTO `creature_text` VALUES ('3679', '0', '0', '%s tosses fitfully in troubled sleep.', '16', '0', '100', '0', '0', '0', 'naralex EMOTE_TROUBLED_SLEEP');
INSERT INTO `creature_text` VALUES ('3678', '8', '0', '%s begins to perform the awakening ritual on Naralex.', '16', '0', '100', '0', '0', '0', 'disciple EMOTE_AWAKENING_RITUAL');
INSERT INTO `creature_text` VALUES ('3678', '7', '0', 'Protect me brave souls as I delve into this Emerald Dream to rescue Naralex and put an end to this corruption!', '12', '0', '100', '0', '0', '0', 'disciple SAY_EMERALD_DREAM');
INSERT INTO `creature_text` VALUES ('3678', '6', '0', 'Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late.', '12', '0', '100', '0', '0', '0', 'disciple SAY_BEYOND_THIS_CORRIDOR');
INSERT INTO `creature_text` VALUES ('3678', '4', '0', 'Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords.', '12', '0', '100', '0', '0', '0', 'disciple SAY_BANISH_THE_SPIRITS');
INSERT INTO `creature_text` VALUES ('3678', '5', '0', 'The caverns have been purified. To Naralex\'s chamber we go!', '12', '0', '100', '0', '0', '0', 'disciple SAY_CAVERNS_PURIFIED');
INSERT INTO `creature_text` VALUES ('3678', '3', '0', 'Come. We must continue. There is much to be done before we can pull Naralex from his nightmare.', '12', '0', '100', '0', '0', '0', 'disciple SAY_MUST_CONTINUE');
INSERT INTO `creature_text` VALUES ('3678', '2', '0', 'These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares.', '12', '0', '100', '0', '0', '0', 'disciple SAY_TEMPLE_OF_PROMISE');
INSERT INTO `creature_text` VALUES ('3678', '0', '0', 'At last! Naralex can be awakened! Come aid me, brave adventurers!', '14', '0', '100', '0', '0', '0', 'disciple SAY_AT_LAST');
INSERT INTO `creature_text` VALUES ('3678', '1', '0', 'I must make the necessary preparations before the awakening ritual can begin. You must protect me!', '12', '0', '100', '0', '0', '0', 'disciple SAY_MAKE_PREPARATIONS');
INSERT INTO `creature_text` VALUES ('7607', '1', '0', 'Ok, here I go!', '12', '0', '100', '0', '0', '0', 'weegli blastfuse SAY_WEEGLI_OK_I_GO');
INSERT INTO `creature_text` VALUES ('7607', '0', '0', 'Oh no!  Here they come!', '12', '0', '100', '0', '0', '0', 'weegli blastfuse SAY_WEEGLI_OHNO');
INSERT INTO `creature_text` VALUES ('29120', '4', '1', 'Come forth my brethren! Feast on their flesh.', '14', '0', '100', '0', '0', '14059', 'anub arak SAY_SUBMERGE_2');
INSERT INTO `creature_text` VALUES ('29120', '4', '0', 'Auum na-l ak-k-k-k, isshhh.', '14', '0', '100', '0', '0', '14058', 'anub arak SAY_SUBMERGE_1');
INSERT INTO `creature_text` VALUES ('29120', '3', '2', 'The pestilence upon you!', '14', '0', '100', '0', '0', '14068', 'anub arak SAY_LOCUST_3');
INSERT INTO `creature_text` VALUES ('29120', '3', '1', 'Uunak-hissss tik-k-k-k-k!', '14', '0', '100', '0', '0', '14067', 'anub arak SAY_LOCUST_2');
INSERT INTO `creature_text` VALUES ('29120', '3', '0', 'Your armor is useless againts my locusts.', '14', '0', '100', '0', '0', '14060', 'anub arak SAY_LOCUST_1');
INSERT INTO `creature_text` VALUES ('29120', '2', '0', 'Ahhh... RAAAAAGH! Never thought... I would be free of him...', '14', '0', '100', '0', '0', '14069', 'anub arak SAY_DEATH');
INSERT INTO `creature_text` VALUES ('29120', '1', '2', 'You shall experience my torment, first-hand!', '14', '0', '100', '0', '0', '14055', 'anub arak SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('29120', '1', '1', 'You have made your choice.', '14', '0', '100', '0', '0', '14056', 'anub arak SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('29120', '0', '0', 'Eternal aggony awaits you!', '14', '0', '100', '0', '0', '14054', 'anub arak SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('29120', '1', '0', 'Soon, the Master\'s voice will call to you.', '14', '0', '100', '0', '0', '14057', 'anub arak SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('28684', '5', '0', 'We are besieged. Strike out and bring back their corpses!', '14', '0', '100', '0', '0', '14079', 'krik thir SAY_SEND_GROUP_1');
INSERT INTO `creature_text` VALUES ('28684', '4', '2', 'I sense the living. Be ready.', '14', '0', '100', '0', '0', '14084', 'krik thir SAY_PREFIGHT_3');
INSERT INTO `creature_text` VALUES ('28684', '4', '1', 'I hear footsteps. Be on your guard.', '14', '0', '100', '0', '0', '14083', 'krik thir SAY_PREFIGHT_2');
INSERT INTO `creature_text` VALUES ('28684', '4', '0', 'Keep an eye on the tunnel. We must not let anyone through!', '14', '0', '100', '0', '0', '14082', 'krik thir SAY_PREFIGHT_1');
INSERT INTO `creature_text` VALUES ('28684', '3', '1', 'Dinner time, my pets.', '14', '0', '100', '0', '0', '14086', 'krik thir SAY_SWARM_2');
INSERT INTO `creature_text` VALUES ('28684', '3', '0', 'They hunger.', '14', '0', '100', '0', '0', '14085', 'krik thir SAY_SWARM_1');
INSERT INTO `creature_text` VALUES ('28684', '2', '0', 'I should be grateful. But I long ago lost the capacity.', '14', '0', '100', '0', '0', '14087', 'krik thir SAY_DEATH');
INSERT INTO `creature_text` VALUES ('28684', '1', '1', 'As Anub\'Arak commands!', '14', '0', '100', '0', '0', '14078', 'krik thir SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('28684', '1', '0', 'You were foolish to come.', '14', '0', '100', '0', '0', '14077', 'krik thir SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('28684', '0', '0', 'This kingdom belongs to the Scourge! Only the dead may enter.', '14', '0', '100', '0', '0', '14075', 'krik thir SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26170', '6', '0', 'Do not worry, Leryssa. I will come back to you when I\'m done. Nothing in the world will stop me from coming home to the only family that I have left.', '12', '0', '100', '0', '0', '0', 'thassarian SAY_THASSARIAN_7');
INSERT INTO `creature_text` VALUES ('26170', '5', '0', 'You might be right, sister. My obligations to my land and King have been fulfilled. But there is still something that I owe to myself.', '12', '0', '100', '0', '0', '0', 'thassarian SAY_THASSARIAN_6');
INSERT INTO `creature_text` VALUES ('26170', '4', '0', 'I cannot return home with you just yet, Leryssa. I am not quite done with the Scourge.', '12', '0', '100', '0', '0', '0', 'thassarian SAY_THASSARIAN_5');
INSERT INTO `creature_text` VALUES ('26170', '3', '0', 'Leryssa... you... you\'re all right!', '12', '0', '100', '0', '0', '0', 'thassarian SAY_THASSARIAN_4');
INSERT INTO `creature_text` VALUES ('28189', '3', '0', 'Yes, my lord!', '12', '0', '100', '0', '0', '0', 'thassarian SAY_TALBOT_4');
INSERT INTO `creature_text` VALUES ('28189', '2', '0', 'Allow me to take care of the intruders, lord. I will feed their entrails to the maggots.', '12', '0', '100', '0', '0', '0', 'thassarian SAY_TALBOT_3');
