INSERT INTO `creature_text` VALUES ('36502', '2', '0', 'Damnation!', '14', '0', '100', '0', '0', '16896', 'devoureur SAY_FACE_SORROW_SLAY_1');
INSERT INTO `creature_text` VALUES ('36502', '0', '0', 'You dare look upon the host of souls? I SHALL DEVOUR YOU WHOLE!', '14', '0', '100', '0', '0', '16884', 'devoureur SAY_FACE_ANGER_AGGRO');
INSERT INTO `creature_text` VALUES ('36502', '0', '1', 'You dare look upon the host of souls? I SHALL DEVOUR YOU WHOLE!', '14', '0', '100', '0', '0', '16890', 'devoureur SAY_FACE_DESIRE_AGGRO');
INSERT INTO `creature_text` VALUES ('25208', '0', '0', 'You can\'t hold me against my will! You\'ve all been brainwashed!', '12', '0', '100', '0', '0', '0', 'Lurgglbr');
INSERT INTO `creature_text` VALUES ('25208', '1', '0', 'Together we will fight our way out of here. Are you ready?', '12', '0', '100', '0', '0', '0', 'Lurgglbr');
INSERT INTO `creature_text` VALUES ('25208', '2', '0', 'This is far enough. I can make it on my own from here.', '12', '0', '100', '0', '0', '0', 'Lurgglbr');
INSERT INTO `creature_text` VALUES ('37597', '6', '0', 'Your first mission is to destroy the machines of death within this malevolent engine of souls, and clear a path for our soldiers.', '12', '0', '100', '0', '0', '16623', 'jaina SAY_INTRO_7');
INSERT INTO `creature_text` VALUES ('37597', '5', '0', 'We will carve a path through this wretched place and find a way to enter the Halls of Reflection. I sense powerful magic hidden away within those walls... Magic that could be the key to destroy the Scourge.', '12', '0', '100', '0', '0', '16622', 'jaina SAY_INTRO_6');
INSERT INTO `creature_text` VALUES ('37597', '4', '0', 'King Varian\'s SI7 agents have gathered information about a private sanctum of the Lich King\'s deep within a place called the Halls of Reflection.', '12', '0', '100', '0', '0', '16621', 'jaina SAY_INTRO_5');
INSERT INTO `creature_text` VALUES ('37597', '3', '0', 'With the attention of the Lich King drawn toward the front gate, we will be working our way through the side in search of information that will enable us to defeat the Scourge - once and for all.', '12', '0', '100', '0', '0', '16620', 'jaina SAY_INTRO_4');
INSERT INTO `creature_text` VALUES ('37597', '2', '0', 'Their success hinges upon what we discover in these cursed halls. Although our mission is a wrought with peril, we must persevere!', '12', '0', '100', '0', '0', '16619', 'jaina SAY_INTRO_3');
INSERT INTO `creature_text` VALUES ('37597', '1', '0', 'Our allies within the Argent Crusade and the Knights of the Ebon Blade have broken through the front gate of Icecrown and are attempting to establish a foothold within the Citadel.', '12', '0', '100', '0', '0', '16618', 'jaina SAY_INTRO_2');
INSERT INTO `creature_text` VALUES ('37597', '0', '0', 'Thank the light for seeing you here safely. We have much work to do if we are to defeat the Lich King and put an end to the Scourge.', '12', '0', '100', '0', '0', '16617', 'jaina SAY_INTRO_1');
INSERT INTO `creature_text` VALUES ('37596', '4', '0', 'Your first mission is to destroy the machines of death within this wretched engine of souls, and clear a path for our soldiers.', '12', '0', '100', '0', '0', '17042', 'sylvanas SAY_INTRO_5');
INSERT INTO `creature_text` VALUES ('37596', '3', '0', 'We will cut a swath of destruction through this cursed place and find a way to enter the Halls of Reflection. If there is anything of value to be found here, it will be found in the Halls.', '12', '0', '100', '0', '0', '17041', 'sylvanas SAY_INTRO_4');
INSERT INTO `creature_text` VALUES ('37596', '2', '0', 'Our scouts have reported that the Lich King has a private chamber, outside of the Frozen Throne, deep within a place called the Halls of Reflection. That is our target, champions.', '12', '0', '100', '0', '0', '17040', 'sylvanas SAY_INTRO_3');
INSERT INTO `creature_text` VALUES ('37596', '1', '0', 'With the attention of the Lich King turned towards the front gate, we\'ll be working our way through the side in search of information that will enable us to defeat the Lich King - once and for all.', '12', '0', '100', '0', '0', '17039', 'sylvanas SAY_INTRO_2');
INSERT INTO `creature_text` VALUES ('37596', '0', '0', 'The Argent Crusade and the Knights of the Ebon Blade have assaulted the gates of Icecrown Citadel and are preparing for a massive attack upon the Scourge. Our missition is a bit more subtle, but equally as important.', '12', '0', '100', '0', '0', '17038', 'sylvanas SAY_INTRO_1');
INSERT INTO `creature_text` VALUES ('37221', '7', '0', 'Tell me how, Uther? How do I destroy my prince? My...', '12', '0', '10', '0', '0', '16638', 'Jaina HoR Alliance Intro 12');
INSERT INTO `creature_text` VALUES ('37225', '4', '0', 'Snap out of it, girl. You must destroy the Lich King at the place where he merged with Ner\'zhul - atop the spire, at the Frozen Throne. It is the only way.', '12', '0', '10', '0', '0', '16670', 'Uther HoR Alliance Intro 13');
INSERT INTO `creature_text` VALUES ('37221', '8', '0', 'You\'re right, Uther. Forgive me. I... I don\'t know what got a hold of me. We will deliver this information to the King and the knights that battle the Scourge within Icecrown Citadel.', '12', '0', '10', '0', '0', '16639', 'Jaina HoR Alliance Intro 14');
INSERT INTO `creature_text` VALUES ('37225', '5', '0', 'There is... something else that you should know about the Lich King. Control over the Scourge must never be lost. Even if you were to strike down the Lich King, another would have to take his place. For without the control of its master, the Scourge would run rampant across the world - destroying all living things.', '12', '0', '10', '1', '0', '16671', 'Uther HoR Alliance Intro 15');
INSERT INTO `creature_text` VALUES ('37225', '6', '0', 'A grand sacrifice by a noble soul...', '12', '0', '10', '1', '0', '16672', 'Uther HoR Alliance Intro 16');
INSERT INTO `creature_text` VALUES ('37221', '9', '0', 'Who could bear such a burden?', '12', '0', '10', '0', '0', '16640', 'Jaina HoR Alliance Intro 17');
INSERT INTO `creature_text` VALUES ('37225', '7', '0', 'I do not know, Jaina. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', '12', '0', '10', '1', '0', '16673', 'Uther HoR Alliance Intro 18');
INSERT INTO `creature_text` VALUES ('37221', '10', '0', 'Then maybe there is still hope...', '12', '0', '10', '0', '0', '16641', 'Jaina HoR Alliance Intro 19');
INSERT INTO `creature_text` VALUES ('37225', '8', '0', 'No, Jaina! ARRRRRRGHHHH... He... He is coming. You... You must...', '12', '0', '10', '0', '0', '16674', 'Uther HoR Alliance Intro 20');
INSERT INTO `creature_text` VALUES ('37223', '0', '0', 'I... I don\'t believe it! Frostmourne stands before us, unguarded! Just as the Gnome claimed. Come, heroes!', '14', '0', '10', '0', '0', '17049', 'Sylvanas HoR Horde Intro 1');
INSERT INTO `creature_text` VALUES ('37223', '1', '0', 'Standing this close to the blade that ended my life... The pain... It is renewed.', '14', '0', '10', '0', '0', '17050', 'Sylvanas HoR Horde Intro 2');
INSERT INTO `creature_text` VALUES ('37223', '2', '0', 'I dare not touch it. Stand back! Stand back as I attempt to commune with the blade! Perhaps our salvation lies within...', '14', '0', '10', '0', '0', '17051', 'Sylvanas HoR Horde Intro 3');
INSERT INTO `creature_text` VALUES ('37225', '9', '0', 'Careful, girl. I\'ve heard talk of that cursed blade saving us before. Look around you and see what has been born of Frostmourne.', '12', '0', '10', '1', '0', '16659', 'Uther HoR Horde Intro 4');
INSERT INTO `creature_text` VALUES ('37223', '3', '0', 'Uther...Uther the Lightbringer. How...', '12', '0', '10', '0', '0', '17052', 'Sylvanas HoR Horde Intro 5');
INSERT INTO `creature_text` VALUES ('37225', '10', '0', 'You haven\'t much time. The Lich King sees what the sword sees. He will be here shortly.', '12', '0', '10', '1', '0', '16660', 'Uther HoR Horde Intro 6');
INSERT INTO `creature_text` VALUES ('37223', '4', '0', 'The Lich King is here? Then my destiny shall be fulfilled today!', '12', '0', '10', '0', '0', '17053', 'Sylvanas HoR Horde Intro 7');
INSERT INTO `creature_text` VALUES ('37225', '11', '0', 'You cannot defeat the Lich King. Not here. You would be a fool to try. He will kill those who follow you and raise them as powerful servants of the Scourge. But for you, Sylvanas, his reward for you would be worse than the last.', '12', '0', '10', '1', '0', '16661', 'Uther HoR Horde Intro 8');
INSERT INTO `creature_text` VALUES ('37223', '5', '0', 'There must be a way... ', '12', '0', '10', '0', '0', '17054', 'Sylvanas HoR Horde Intro 9');
INSERT INTO `creature_text` VALUES ('37225', '12', '0', 'Perhaps, but know this: there must always be a Lich King. Even if you were to strike down Arthas, another would have to take his place, for without the control of the Lich King, the Scourge would wash over this world like locusts, destroying all that they touched.', '12', '0', '10', '1', '0', '16662', 'Uther HoR Horde Intro 10');
INSERT INTO `creature_text` VALUES ('37223', '6', '0', 'Who could bear such a burden?', '12', '0', '10', '0', '0', '17055', 'Sylvanas HoR Horde Intro 11');
INSERT INTO `creature_text` VALUES ('37225', '13', '0', 'I do not know, Banshee Queen. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', '12', '0', '10', '1', '0', '16663', 'Uther HoR Horde Intro 12');
INSERT INTO `creature_text` VALUES ('37225', '14', '0', 'Alas, the only way to defeat the Lich King is to destroy him at the place he was created.', '12', '0', '10', '1', '0', '16664', 'Uther HoR Horde Intro 13');
INSERT INTO `creature_text` VALUES ('37223', '7', '0', 'The Frozen Throne...', '12', '0', '10', '0', '0', '17056', 'Sylvanas HoR Horde Intro 14');
INSERT INTO `creature_text` VALUES ('37225', '15', '0', 'Aye. ARRRRRRGHHHH... He... He is coming. You... You must...', '12', '0', '10', '0', '0', '16665', 'Uther HoR Horde Intro 15');
INSERT INTO `creature_text` VALUES ('36954', '10', '0', 'All is lost!', '14', '0', '100', '0', '0', '17215', 'Lich King SAY_LICH_KING_WIN');
INSERT INTO `creature_text` VALUES ('36955', '0', '0', 'He is too powerful, we must leave this place at once! My magic will hold him in place for only a short time! Come quickly, heroes!', '14', '0', '100', '1', '0', '16644', 'Jaina SAY_JAINA_AGGRO');
INSERT INTO `creature_text` VALUES ('36955', '1', '0', 'I will destroy this barrier. You must hold the undead back!', '14', '0', '100', '0', '0', '16607', 'Jaina SAY_JAINA_WALL_01');
INSERT INTO `creature_text` VALUES ('38112', '5', '0', 'As you wish, my lord.', '14', '0', '10', '2', '0', '16717', 'HoR Intro LK 4');
INSERT INTO `creature_text` VALUES ('38113', '5', '0', 'As you wish, my lord.', '14', '0', '10', '0', '0', '16741', 'HoR Intro LK 5');
INSERT INTO `creature_text` VALUES ('37225', '0', '0', 'Jaina! Could it truly be you?', '12', '0', '10', '0', '0', '16666', 'Uther HoR Alliance Intro 5');
INSERT INTO `creature_text` VALUES ('37221', '4', '0', 'Uther! Dear Uther! I... I\'m so sorry.', '12', '0', '10', '0', '0', '16635', 'Jaina HoR Alliance Intro 6');
INSERT INTO `creature_text` VALUES ('37225', '1', '0', 'Jaina you haven\'t much time. The Lich King sees what the sword sees. He will be here shortly!', '12', '0', '10', '1', '0', '16667', 'Uther HoR Alliance Intro 7');
INSERT INTO `creature_text` VALUES ('37221', '5', '0', 'Arthas is here? Maybe I...', '12', '0', '10', '0', '0', '16636', 'Jaina HoR Alliance Intro 8');
INSERT INTO `creature_text` VALUES ('37225', '2', '0', 'No, girl. Arthas is not here. Arthas is merely a presence within the Lich King\'s mind. A dwindling presence...', '12', '0', '10', '1', '0', '16668', 'Uther HoR Alliance Intro 9');
INSERT INTO `creature_text` VALUES ('37221', '6', '0', 'But Uther, if there\'s any hope of reaching Arthas. I... I must try.', '12', '0', '10', '0', '0', '16637', 'Jaina HoR Alliance Intro 10');
INSERT INTO `creature_text` VALUES ('37225', '3', '0', 'Jaina, listen to me. You must destroy the Lich King. You cannot reason with him. He will kill you and your allies and raise you all as powerful soldiers of the Scourge.', '12', '0', '10', '1', '0', '16669', 'Uther HoR Alliance Intro 11');
INSERT INTO `creature_text` VALUES ('36477', '7', '0', 'What you seek is in the master\'s lair, but you must destroy Tyrannus to gain entry. Within the Halls of Reflection you will find Frostmourne. It... it holds the truth.', '14', '0', '100', '0', '0', '16935', 'Krick SAY_KRICK_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36477', '6', '0', 'Wait! Stop! Don\'t kill me, please! I\'ll tell you everything!', '14', '0', '100', '0', '0', '16934', 'Krick SAY_KRICK_OUTRO_1');
INSERT INTO `creature_text` VALUES ('36477', '5', '2', 'What are you attacking him for? The dangerous one is over there,fool!', '14', '0', '100', '0', '0', '16933', 'Krick SAY_CHASE_3');
INSERT INTO `creature_text` VALUES ('36477', '5', '1', 'I\'ve changed my mind...go get that one instead!', '14', '0', '100', '0', '0', '16932', 'Krick SAY_CHASE_2');
INSERT INTO `creature_text` VALUES ('36477', '5', '0', 'No! That one! That one! Get that one!', '14', '0', '100', '0', '0', '16931', 'Krick SAY_CHASE_1');
INSERT INTO `creature_text` VALUES ('36477', '4', '0', 'Quickly! Poison them all while they\'re still close!', '14', '0', '100', '0', '0', '16930', 'Krick SAY_POISON_NOVA');
INSERT INTO `creature_text` VALUES ('36477', '3', '0', 'Krick begins rapidly conjuring explosive mines!', '41', '0', '100', '0', '0', '0', 'Krick SAY_BARRAGE_2');
INSERT INTO `creature_text` VALUES ('36477', '2', '0', 'Enough moving around! Hold still while I blow them all up!', '14', '0', '100', '0', '0', '16929', 'Krick SAY_BARRAGE_1');
INSERT INTO `creature_text` VALUES ('36477', '1', '0', 'Ooh...We could probably use these parts!', '14', '0', '100', '0', '0', '16927', 'Krick SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('36477', '1', '1', 'Arms and legs are in short supply...Thanks for your contribution!', '14', '0', '100', '0', '0', '16928', 'Krick SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('36477', '0', '0', 'Our work must not be interrupted! Ick! Take care of them!', '14', '0', '100', '0', '0', '16926', 'Krick SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36476', '1', '0', 'Ick is chasing you!', '41', '0', '100', '0', '0', '0', 'Ick SAY_ICK_CHASE_1');
INSERT INTO `creature_text` VALUES ('36476', '0', '0', 'Ick begins to unleash a toxic poison cloud!', '41', '0', '100', '0', '0', '0', 'Ick SAY_ICK_POISON_NOVA');
INSERT INTO `creature_text` VALUES ('36658', '11', '0', 'Rimefang, destroy this fool!', '14', '0', '100', '0', '0', '16764', 'Tyrannus SAY_MARK_RIMEFANG_1');
INSERT INTO `creature_text` VALUES ('36658', '10', '0', 'Impossible! Rimefang... Warn...', '14', '0', '100', '0', '0', '16763', 'Tyrannus SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36658', '9', '1', 'Perhaps you should have stayed in the mountains!', '14', '0', '100', '0', '0', '16762', 'Tyrannus SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('36658', '9', '0', 'Such a shameful display... You are better off dead!', '14', '0', '100', '0', '0', '16761', 'Tyrannus SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('36658', '8', '0', 'I shall not fail The Lich King! Come and meet your end!', '14', '0', '100', '0', '0', '16760', 'Tyrannus SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36658', '7', '0', 'Ha, such an amusing gesture from the rabble. When I have finished with you, my master\'s blade will feast upon your souls. Die!', '14', '0', '100', '0', '0', '16759', 'Tyrannus SAY_TYRANNUS_INTRO_3');
INSERT INTO `creature_text` VALUES ('36658', '6', '0', 'Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise.', '14', '0', '100', '0', '0', '16758', 'Tyrannus SAY_TYRANNUS_INTRO_1');
INSERT INTO `creature_text` VALUES ('36658', '5', '0', 'Rimefang! Trap them within the tunnel! Bury them alive!', '14', '0', '100', '0', '0', '16757', 'Tyrannus SAY_GAUNTLET_START');
INSERT INTO `creature_text` VALUES ('36658', '4', '0', 'Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them!', '14', '0', '100', '0', '0', '16756', 'Tyrannus SAY_AMBUSH_2');
INSERT INTO `creature_text` VALUES ('36658', '3', '0', 'Your pursuit shall be in vain, adventurers, for the Lich King has placed an army of undead at my command! Behold!', '14', '0', '100', '0', '0', '16755', 'Tyrannus SAY_AMBUSH_1');
INSERT INTO `creature_text` VALUES ('36794', '2', '0', 'Do not think that I shall permit you entry into my master\'s sanctum so easily. Pursue me if you dare.', '14', '0', '100', '0', '0', '16754', 'Tyrannus SAY_TYRANNUS_OUTRO_9');
INSERT INTO `creature_text` VALUES ('36794', '1', '0', 'Worthless gnat! Death is all that awaits you!', '14', '0', '100', '0', '0', '16753', 'Tyrannus SAY_TYRANNUS_OUTRO_7');
INSERT INTO `creature_text` VALUES ('36658', '0', '0', 'Another shall take his place. You waste your time.', '14', '0', '100', '0', '0', '16752', 'Tyrannus SAY_TYRANNUS_DEATH');
INSERT INTO `creature_text` VALUES ('37592', '2', '0', 'This day will stand as a testament not only to your valor, but to the fact that no foe, not even the Lich King himself, can stand when Alliance and Horde set aside their differences and ---', '14', '0', '100', '0', '0', '0', 'Gorkun SAY_GORKUN_OUTRO_2');
INSERT INTO `creature_text` VALUES ('37592', '0', '0', 'Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord!', '14', '0', '100', '0', '0', '17150', 'Gorkun SAY_GORKUN_INTRO_2');
INSERT INTO `creature_text` VALUES ('37592', '1', '0', 'Brave champions, we owe you our lives, our freedom... Though it be a tiny gesture in the face of this enormous debt, I pledge that from this day forth, all will know of your deeds, and the blazing path of light you cut through the shadow of this dark citadel.', '14', '0', '100', '0', '0', '0', 'Gorkun SAY_GORKUN_OUTRO_1');
INSERT INTO `creature_text` VALUES ('36993', '4', '0', 'The Frost Queen is gone. We must keep moving - our objective is near.', '12', '0', '100', '0', '0', '16615', 'Jaina SAY_JAYNA_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36993', '3', '0', 'Heroes, to me!', '14', '0', '100', '0', '0', '16614', 'Jaina SAY_JAYNA_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36993', '2', '0', 'What a cruel end. Come, heroes. We must see if the gnome\'s story is true. If we can separate Arthas from Frostmourne, we might have a chance at stopping him.', '14', '0', '100', '0', '0', '16613', 'Jaina SAY_JAINA_OUTRO_10');
INSERT INTO `creature_text` VALUES ('36993', '1', '0', 'Frostmourne lies unguarded? Impossible!', '14', '0', '100', '0', '0', '16612', 'Jaina SAY_JAINA_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36993', '0', '0', 'I\'m not so naive as to believe your appeal for clemency, but I will listen.', '14', '0', '100', '0', '0', '16611', 'Jaina SAY_JAINA_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36990', '3', '0', 'Take cover behind me! Quickly!', '14', '0', '100', '0', '0', '17037', 'Sylvanas SAY_SYLVANAS_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36990', '4', '0', 'I thought he\'d never shut up. At last, Sindragosa silenced that long-winded fool. To the Halls of Reflection, champions! Our objective is near... I can sense it.', '12', '0', '100', '0', '0', '17036', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36990', '2', '0', 'A fitting end for a traitor. Come, we must free the slaves and see what is within the Lich King\'s chamber for ourselves.', '14', '0', '100', '0', '0', '17035', 'Sylvanas SAY_SYLVANAS_OUTRO_10');
INSERT INTO `creature_text` VALUES ('36990', '0', '0', 'Why should the Banshee Queen spare your miserable life?', '14', '0', '100', '0', '0', '17033', 'Sylvanas SAY_SYLVANAS_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36990', '1', '0', 'Frostmourne? The Lich King is never without his blade! If you are lying to me...', '14', '0', '100', '0', '0', '17034', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES ('26588', '10', '0', 'The Ravenous Worg chomps down on Mr. Floppy', '15', '0', '100', '0', '0', '0', 'emily TEXT_EMOTE_AGGRO');
INSERT INTO `creature_text` VALUES ('26588', '9', '0', 'Mr. Floppy revives', '15', '0', '100', '0', '0', '0', 'emily TEXT_EMOTE_WP1');
INSERT INTO `creature_text` VALUES ('26588', '8', '0', 'I think I see the camp! We\'re almost home, Mr. Floppy! Let\'s go!', '12', '0', '100', '0', '0', '0', 'emily SAY_VICTORY4');
INSERT INTO `creature_text` VALUES ('26588', '7', '0', 'Mr. Floppy, you\'re ok! Thank you so much for saving Mr. Floppy!', '12', '0', '100', '0', '0', '0', 'emily SAY_VICTORY3');
INSERT INTO `creature_text` VALUES ('26588', '6', '0', 'Don\'t go toward the light, Mr. Floppy!', '12', '0', '100', '0', '0', '0', 'emily SAY_VICTORY2');
INSERT INTO `creature_text` VALUES ('26588', '5', '0', 'Let\'s get out of here before more wolves find us!', '12', '0', '100', '0', '0', '0', 'emily SAY_VICTORY1');
INSERT INTO `creature_text` VALUES ('26588', '4', '0', 'There\'s a big meanie attacking Mr. Floppy! Help! ', '12', '0', '100', '0', '0', '0', 'emily SAY_RANDOMAGGRO');
INSERT INTO `creature_text` VALUES ('26588', '3', '0', 'He\'s gonna eat Mr. Floppy! You gotta help Mr. Floppy! You just gotta!', '12', '0', '100', '0', '0', '0', 'emily SAY_WORGRAGGRO4');
INSERT INTO `creature_text` VALUES ('26588', '1', '0', 'He\'s going for Mr. Floppy! ', '12', '0', '100', '0', '0', '0', 'emily SAY_WORGHAGGRO2');
