INSERT INTO `creature_text` VALUES ('15990', '14', '0', 'Minions, servants, soldiers of the cold dark! Obey the call of Kel\'Thuzad!', '14', '0', '100', '0', '0', '14471', 'kelthuzad SAY_SUMMON_MINIONS');
INSERT INTO `creature_text` VALUES ('15990', '15', '0', 'Your petty magics are no challenge to the might of the Scourge! ', '14', '0', '100', '0', '0', '14481', 'kelthuzad SAY_SPECIAL1_MANA_DET');
