INSERT INTO `creature_text` VALUES ('30395', '0', '0', 'My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest.', '12', '0', '100', '1', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('12116', '22', '0', 'Without a purpose, they wander... But many find their way back to the sacred areas that they once were sworn to protect.', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_17');
INSERT INTO `creature_text` VALUES ('12116', '23', '0', 'Wildkin are inherently magical; this power was bestowed upon them by the goddess.', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_18');
INSERT INTO `creature_text` VALUES ('12116', '24', '0', 'Know that wherever you might find them in the world, they are protecting something of importance, as they were entrusted to do so long ago.', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_19');
INSERT INTO `creature_text` VALUES ('12116', '25', '0', 'Please, remember what we have shown you...', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_20');
INSERT INTO `creature_text` VALUES ('10300', '0', '0', 'Remember, I need your help to properly channel. I will ask you to aid me several times in our path, so please be ready.', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_QUEST_START');
INSERT INTO `creature_text` VALUES ('10300', '1', '0', 'This blue light... It\'s strange. What do you think it means?', '12', '0', '100', '25', '0', '0', 'Ranshalla SAY_ENTER_OWL_THICKET');
INSERT INTO `creature_text` VALUES ('10300', '2', '0', 'We\'ve found it!', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_1');
INSERT INTO `creature_text` VALUES ('10300', '2', '1', 'Please, light this while I am channeling', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_2');
INSERT INTO `creature_text` VALUES ('10300', '2', '2', 'This is the place. Let\'s light it.', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_3');
INSERT INTO `creature_text` VALUES ('10300', '3', '0', 'Let\'s find the next one.', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_AFTER_TORCH_1');
INSERT INTO `creature_text` VALUES ('10300', '3', '1', 'We must continue on now.', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_AFTER_TORCH_2');
INSERT INTO `creature_text` VALUES ('10300', '4', '0', 'It is time for the final step; we must activate the altar.', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_REACH_ALTAR_1');
INSERT INTO `creature_text` VALUES ('10300', '5', '0', 'I will read the words carved into the stone, and you must find a way to light it.', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_REACH_ALTAR_2');
INSERT INTO `creature_text` VALUES ('10300', '6', '0', 'The altar is glowing! We have done it!', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_1');
INSERT INTO `creature_text` VALUES ('10300', '7', '0', 'What is happening? Look!', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_2');
INSERT INTO `creature_text` VALUES ('12116', '8', '0', 'It has been many years...', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_3');
INSERT INTO `creature_text` VALUES ('12116', '9', '0', 'Who has disturbed the altar of the goddess?', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_4');
INSERT INTO `creature_text` VALUES ('10300', '10', '0', 'Please, priestesses, forgive us for our intrusion. We do not wish any harm here.', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_5');
INSERT INTO `creature_text` VALUES ('10300', '11', '0', 'We only wish to know why the wildkin guard this area...', '12', '0', '100', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_6');
INSERT INTO `creature_text` VALUES ('12116', '12', '0', 'Enu thora\'serador. This is a sacred place.', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_7');
INSERT INTO `creature_text` VALUES ('12116', '13', '0', 'We will show you...', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_8');
INSERT INTO `creature_text` VALUES ('12116', '14', '0', 'Look above you; thara dormil dorah...', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_9');
INSERT INTO `creature_text` VALUES ('12116', '15', '0', 'This gem once allowed direct communication with Elune, herself.', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_10');
INSERT INTO `creature_text` VALUES ('12116', '16', '0', 'Through the gem, Elune channeled her infinite wisdom...', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_11');
INSERT INTO `creature_text` VALUES ('12116', '17', '0', 'Realizing that the gem needed to be protected, we turned to the goddess herself.', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_12');
INSERT INTO `creature_text` VALUES ('12116', '18', '0', 'Soon after, we began to have visions of a creature... A creature with the feathers of an owl, but the will and might of a bear...', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_13');
INSERT INTO `creature_text` VALUES ('12116', '19', '0', 'It was on that day that the wildkin were given to us. Fierce guardians, the goddess assigned the wildkin to protect all of her sacred places.', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_14');
INSERT INTO `creature_text` VALUES ('12152', '20', '0', 'Anu\'dorini Talah, Ru shallora enudoril.', '12', '0', '100', '0', '0', '0', 'Voice of Elune SAY_VOICE_ALTAR_15');
INSERT INTO `creature_text` VALUES ('12116', '21', '0', 'But now, many years later, the wildkin have grown more feral, and without the guidance of the goddess, they are confused...', '12', '0', '100', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_16');
INSERT INTO `creature_text` VALUES ('9026', '0', '0', 'I will be reborn, mortals! Incendius shall raise me from these ashes!', '12', '0', '100', '0', '0', '0', 'Overmaster Pyron');
INSERT INTO `creature_text` VALUES ('1494', '0', '0', 'Mmmh...I SMELL FOOD!', '14', '0', '100', '0', '0', '0', 'Negolash Yell on Summon');
INSERT INTO `creature_text` VALUES ('33288', '6', '0', 'Your fate is sealed. The end of days is finally upon you and ALL who inhabit this miserable little seedling. Uulwi ifis halahs gag erh\'ongg w\'ssh.', '14', '0', '100', '0', '0', '15761', 'YoggSaron SAY_DEATH');
INSERT INTO `creature_text` VALUES ('28952', '0', '0', 'I\'M FREE, LITTLE DRAKKARI. TIME TO PAY FOR YOUR MANY TRESSPASSES!', '14', '0', '100', '33', '0', '771', 'Akali');
INSERT INTO `creature_text` VALUES ('28996', '0', '0', 'ENOUGH!', '14', '0', '100', '15', '0', '0', 'Prophet of Akali');
INSERT INTO `creature_text` VALUES ('28996', '1', '0', 'And now, Akali, my master will have your blood and I will take some of your power as well!', '14', '0', '100', '397', '0', '0', 'Prophet of Akali');
INSERT INTO `creature_text` VALUES ('28996', '2', '0', 'It is done. Run back to your masters. Run back to Har\'koa. Tell her that I am coming!', '14', '0', '100', '14', '0', '0', 'Prophet of Akali');
INSERT INTO `creature_text` VALUES ('29024', '0', '0', 'The unblemished heart of the guardian must be secured to show defiance and strength. Jealous Zim\'Rhuk keeps those who harbor it.', '12', '0', '100', '0', '0', '473', 'Nalorakk');
INSERT INTO `creature_text` VALUES ('29023', '0', '0', 'The gusty essence of deceased Quetz\'lun\'s wardens must be gathered to draw him near. To Har\'koa must both of these rarities be delivered.', '12', '0', '100', '0', '0', '9919', 'Jana\'lai');
INSERT INTO `creature_text` VALUES ('29022', '0', '0', 'Once more we hear your call, Khufu. We have wisdom for you and $N. You face great peril and must act swiftly or you all and Har\'koa will die.', '12', '0', '100', '0', '0', '643', 'Halazzi');
INSERT INTO `creature_text` VALUES ('28401', '1', '0', 'No, not Akali too!', '15', '0', '100', '0', '0', '0', 'Har\'koa');
INSERT INTO `creature_text` VALUES ('29021', '0', '0', 'And yet we see only doom in your future. It is your destiny that you do this, $N, but we fear that the prophet is too powerful for you and your friends. Still, you must try.', '12', '0', '100', '0', '0', '12196', 'Akil\'zon');
INSERT INTO `creature_text` VALUES ('33113', '11', '0', 'System malfunction. Diverting power to support systems.', '14', '0', '0', '0', '0', '15517', 'Flame Leviathan SAY_OVERLOAD_1');
