INSERT INTO `creature_text` VALUES ('28214', '5', '0', 'Frenzyheart feed Kartak big-tongue so Kartak not eat us. That why we smarter than you, slave thing.', '12', '0', '100', '0', '0', '0', 'Goregek');
INSERT INTO `creature_text` VALUES ('28215', '0', '0', 'Frenzyheart came here not long ago for new home... big-tongue homes pretty nice, so we maybe take those too. They ugly anyway.', '12', '0', '12', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '0', '1', 'Lot of strong Frenzyheart here. Dajik taught most of them. First time I teach something like you though. You not a Frenzyheart, you know.', '12', '0', '12', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '0', '2', 'Let\'s just eat bristlepine instead, okay? All done with chasing.', '12', '0', '12', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '0', '3', 'There reason Frenzyheart not chase back chickens. Look kind of dumb to lose to chicken.', '12', '0', '12', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '0', '4', 'Maybe you not notice, chicken move kind of fast.', '12', '0', '13', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '0', '5', 'Time for more smart Dajik tip! If Goregek let chickens out again, make Goregek get chickens back again.', '12', '0', '13', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '0', '6', 'I placed most of the spikes. Good amount of pointiness I think. You like?', '12', '0', '13', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '0', '7', 'Oh! Dajik has idea! Make chicken sounds and maybe they come to you!', '12', '0', '13', '0', '0', '0', 'Dajik');
