INSERT INTO `creature_text` VALUES ('28215', '1', '1', 'Did you see face? Looked so scared, \"Ah, big thing going to smash me!\" Hah.', '12', '0', '20', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '1', '2', 'Hah! Maybe you learn a little after all!', '12', '0', '20', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '1', '3', 'Yes! Stab few more time just in case! Stab good!', '12', '0', '20', '0', '0', '0', 'Dajik');
