INSERT INTO `creature_text` VALUES ('28215', '2', '3', 'I was going to draw picture of best attack plan... but you have my chalk.', '12', '0', '20', '0', '0', '0', 'Dajik');
INSERT INTO `creature_text` VALUES ('28215', '2', '4', 'Time for more smart Dajik tip! Make sure thing dead before reach in mouth for loots.', '12', '0', '20', '0', '0', '0', 'Dajik');
