INSERT INTO `creature_text` VALUES ('35451', '3', '0', 'This farce ends here!', '14', '0', '0', '0', '0', '16259', 'Black Knight Aggro');
INSERT INTO `creature_text` VALUES ('34990', '12', '0', 'Don\'t just stand there; kill him!', '12', '0', '0', '2', '0', '0', 'ToC5 - Black Knight Aggro Alliance');
INSERT INTO `creature_text` VALUES ('34995', '12', '0', 'Tear him apart!', '12', '0', '0', '2', '0', '0', 'ToC5 - Black Knight Aggro Horde');
INSERT INTO `creature_text` VALUES ('35451', '4', '0', 'A waste of flesh.', '14', '0', '0', '0', '0', '16261', 'Black Knight Kills Player');
INSERT INTO `creature_text` VALUES ('35451', '4', '1', 'Pathetic.', '14', '0', '0', '0', '0', '16260', 'Black Knight Kills Player');
INSERT INTO `creature_text` VALUES ('35451', '5', '0', 'My roting flash was just getting in the way!', '14', '0', '0', '0', '0', '16262', 'Black Knight Phase 2');
INSERT INTO `creature_text` VALUES ('35451', '6', '0', 'I have no need for bones to best you!', '14', '0', '0', '0', '0', '16263', 'Black Knight Phase 2');
INSERT INTO `creature_text` VALUES ('35451', '7', '0', 'No! I must not fail...again...', '14', '0', '0', '0', '0', '16264', 'Black Knight Dies');
INSERT INTO `creature_text` VALUES ('34994', '12', '0', 'Well done horde!', '12', '0', '0', '2', '0', '0', 'ToC5 - Black Knight dies horde');
INSERT INTO `creature_text` VALUES ('34996', '23', '0', 'My congratulations, champions.  Through trials both planned and unexpected, you have triumphed.', '12', '0', '0', '2', '0', '0', 'ToC5 - Black Knight Dies Alliance 1');
INSERT INTO `creature_text` VALUES ('34996', '24', '0', 'Go now and rest; you\'ve earned it.', '12', '0', '0', '2', '0', '0', 'ToC5 - Black Knight Dies Alliance 2');
INSERT INTO `creature_text` VALUES ('34990', '13', '0', 'You fought well.', '12', '0', '0', '2', '0', '0', 'ToC5 - Black Knight Dies Alliance 3');
INSERT INTO `creature_text` VALUES ('33288', '5', '0', 'Your will is no longer you own...', '42', '0', '100', '0', '0', '15759', 'YoggSaron WHISP_INSANITY_1');
INSERT INTO `creature_text` VALUES ('33288', '4', '0', 'Eternal suffering awaits!', '14', '0', '100', '0', '0', '15758', 'YoggSaron SAY_DEAFENING_ROAR');
INSERT INTO `creature_text` VALUES ('33288', '3', '0', 'Hoohehehahahaha... AHAHAHAHAHAHA!', '14', '0', '100', '0', '0', '15757', 'YoggSaron SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('33288', '2', '0', 'MADNESS WILL CONSUME YOU!', '14', '0', '100', '0', '0', '15756', 'YoggSaron SAY_PORTAL');
INSERT INTO `creature_text` VALUES ('33288', '1', '0', 'Look upon the true face of death and know that your end comes soon!', '14', '0', '100', '0', '0', '15755', 'YoggSaron SAY_PHASE3');
INSERT INTO `creature_text` VALUES ('33280', '1', '0', 'Your will is no longer your own...', '15', '0', '100', '0', '0', '15759', 'Voice of Yogg-Saron - WHISPER_VOICE_INSANE');
INSERT INTO `creature_text` VALUES ('33280', '0', '0', 'Weak-minded fools!', '15', '0', '100', '0', '0', '15780', 'Voice of Yogg-Saron - WHISPER_VOICE_PHASE_1_WIPE');
INSERT INTO `creature_text` VALUES ('33495', '0', '0', 'That terrible glow... should that be?', '14', '0', '100', '0', '0', '15784', 'Ysera v3 2');
INSERT INTO `creature_text` VALUES ('33523', '1', '0', 'For it to be as it must, yes.', '14', '0', '100', '0', '0', '15632', 'Neltharion v3 3');
INSERT INTO `creature_text` VALUES ('33523', '0', '0', 'It is done... All have been given that which must be given. I now seal the Dragon Soul forever...', '14', '0', '100', '0', '0', '15631', 'Neltharion v3 1');
INSERT INTO `creature_text` VALUES ('33436', '3', '0', 'The orc leaders agree with your assessment.', '14', '0', '100', '0', '0', '15541', 'Garona v1 8');
INSERT INTO `creature_text` VALUES ('33437', '0', '0', 'We will hold until the reinforcements come. As long as men with stout hearts are manning the walls and throne Stormwind will hold.', '14', '0', '100', '0', '0', '15585', 'King Llane v1 7');
INSERT INTO `creature_text` VALUES ('33436', '2', '0', 'Gul\'dan is bringing up his warlocks by nightfall. Until then, the Blackrock clan will be trying to take the Eastern Wall.', '14', '0', '100', '0', '0', '15540', 'Garona v1 6');
INSERT INTO `creature_text` VALUES ('31254', '0', '1', 'The rumors were true!  The prince has gone mad!', '12', '0', '10', '0', '0', '0', 'Lordaeron Footsoldier to Ghoulish Minion');
INSERT INTO `creature_text` VALUES ('31254', '0', '0', 'The undead are upon us!', '12', '0', '10', '0', '0', '0', 'Lordaeron Footsoldier to Ghoulish Minion');
INSERT INTO `creature_text` VALUES ('32443', '0', '0', 'Rise, Sindragosa! Rise and lead the Frostbrood into war!', '12', '0', '100', '16', '0', '0', 'Lich King');
INSERT INTO `creature_text` VALUES ('32588', '0', '0', 'Prepare to die!', '12', '0', '100', '0', '0', '0', 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES ('26648', '8', '0', 'Now swim back with the knowledge I have granted you. Do what you can for them, $r.', '15', '0', '100', '0', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '9', '0', 'Very well, $N, you have failed to act. The prophecy is not yours to learn. Do not call upon me again until you have found your backbone.', '14', '0', '100', '33', '0', '11557', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '7', '0', 'Tell the mystic that his people are to stand and fight alongside the Horde and Alliance against the forces of Malygos and the Lich King.', '15', '0', '100', '0', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '6', '0', 'Simply put, you must tell the tuskarr that they cannot run. If they do so, their spirits will be destroyed by the evil rising within Northrend.', '15', '0', '100', '0', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '5', '0', 'Well done, $N. Your display of respect is duly noted. Now, I have information for you that you must convey to the Kalu\'ak.', '14', '0', '100', '33', '0', '11557', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '4', '0', 'I\'m rather curious as to what will happen, should you ignore this and NOT jump into the water.', '14', '0', '100', '33', '0', '11557', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '3', '0', 'Though you are compelled, the choice, and the last step before you leap, are yours. You have twenty more seconds to decide.', '14', '0', '100', '33', '0', '11557', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '2', '0', 'I will lay a mild compulsion upon you. Jump into the depths before me so that you put yourself into my element and thereby display your submission.', '14', '0', '100', '33', '0', '11557', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '1', '0', 'I sense uncertainty in you, and I do not trust it whether you are with them, or not. If you wish my augury for the Kalu\'ak, you will have to prove yourself first.', '14', '0', '100', '33', '0', '11557', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '0', '0', 'Little $N, why do you call me forth? Are you working with the trolls of this land? Have you come to kill me and take my power as your own?', '14', '0', '100', '33', '0', '11557', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('30299', '1', '0', 'You may have killed Valduran, but that will not stop me from completing the colossus.', '12', '0', '100', '1', '0', '0', 'Overseer Narvir');
INSERT INTO `creature_text` VALUES ('30299', '0', '0', 'You didn\'t think that I was going to let you walk in here and take the Heart of the Storm, did you?', '12', '0', '100', '6', '0', '0', 'Overseer Narvir');
INSERT INTO `creature_text` VALUES ('36954', '9', '0', 'Nowhere to run! You\'re mine now...', '14', '0', '100', '0', '0', '17223', 'Lich King SAY_LICH_KING_END_DUN');
INSERT INTO `creature_text` VALUES ('36954', '7', '0', 'Minions sees them. Bring their corpses back to me!', '14', '0', '100', '0', '0', '17222', 'Lich King SAY_LICH_KING_ABON');
INSERT INTO `creature_text` VALUES ('36954', '6', '0', 'Rise minions, do not left them us!', '14', '0', '100', '0', '0', '17216', 'Lich King SAY_LICH_KING_GNOUL');
INSERT INTO `creature_text` VALUES ('36954', '4', '0', 'How long can you fight it?', '14', '0', '100', '0', '0', '17220', 'Lich King SAY_LICH_KING_WALL_04');
INSERT INTO `creature_text` VALUES ('36954', '5', '0', 'There is no escape!', '14', '0', '100', '0', '0', '17217', 'Lich King SAY_LICH_KING_WALL_01');
INSERT INTO `creature_text` VALUES ('37226', '3', '0', 'Foolish girl. You seek that, which I killed long ago. He is nearly a ghost now. A fate ago in my mind.', '14', '0', '100', '0', '0', '17229', 'Lich King SAY_LK_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES ('37226', '4', '0', 'I will not make the same mistake again. This time there will be no escape. You fail to serve me in undead. Now, all that remains for you is oblivion!', '14', '0', '100', '0', '0', '17228', 'Lich King SAY_LK_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES ('36723', '0', '0', 'You are not worthy to face the Lich King!', '14', '0', '100', '0', '0', '16921', 'general SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36723', '1', '0', 'Master, I have failed...', '14', '0', '100', '0', '0', '16922', 'general SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36954', '0', '0', 'Your allies have arrived, Jaina, just as you promised. You will all become powerful agents of the Scourge.', '14', '0', '100', '0', '0', '17212', 'Lich King SAY_LICH_KING_AGGRO_A');
INSERT INTO `creature_text` VALUES ('36954', '1', '0', 'I will not make the same mistake again, Sylvanas. This time there will be no escape. You will all serve me in death!', '14', '0', '100', '0', '0', '17213', 'Lich King SAY_LICH_KING_AGGRO_H');
INSERT INTO `creature_text` VALUES ('36954', '2', '0', 'Succumb to the chill of the grave.', '14', '0', '100', '0', '0', '17218', 'Lich King SAY_LICH_KING_WALL_02');
INSERT INTO `creature_text` VALUES ('36954', '3', '0', 'Another dead end.', '14', '0', '100', '0', '0', '17219', 'Lich King SAY_LICH_KING_WALL_03');
INSERT INTO `creature_text` VALUES ('21409', '6', '0', 'Ah, damn it all! Clever trap. Too bad you\'ll never live to tell the tale...', '12', '0', '100', '1', '0', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '5', '0', 'Envoy Icarius puts the bundle of bloodthistle in his robe.', '16', '0', '100', '0', '0', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('27851', '0', '0', 'The walls of Frostmourne Cavern shudder and shake.', '41', '0', '100', '0', '0', '0', 'Thel\'zan Spell Dummy');
INSERT INTO `creature_text` VALUES ('27480', '5', '0', 'Who... Who am I?', '12', '0', '100', '0', '0', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '4', '0', 'O\' my head... Wh... Where am I?', '12', '0', '100', '0', '0', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '3', '0', 'Leave it be, Arthas. Forget this business and lead your men home.', '12', '0', '100', '396', '0', '12736', 'Muradin');
INSERT INTO `creature_text` VALUES ('29542', '0', '0', 'I will not die so quickly this time!', '14', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29919', '2', '0', 'What is this?', '14', '0', '100', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '3', '0', 'My answer? Here\'s my answer, little messenger!', '14', '0', '100', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '4', '0', 'I will feed you to the dogs!', '14', '0', '100', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('30955', '0', '0', 'This? This is me taking control of you. This is me setting up my final revenge!', '14', '0', '100', '457', '0', '13824', 'Lady Nightswood');
INSERT INTO `creature_text` VALUES ('30945', '4', '0', 'Ahahahahahaha! It is done. Return to my cave. We have much to discuss!', '14', '0', '100', '457', '0', '1167', 'Vardmadra to ');
INSERT INTO `creature_text` VALUES ('30945', '3', '0', 'Stay away from me creature! Do not touch me!', '14', '0', '100', '0', '0', '1168', 'Vardmadra to Nightswood');
INSERT INTO `creature_text` VALUES ('30945', '2', '0', 'I know not how this was possible, but you must still be judged Iskalder. Wait... what is this?', '14', '0', '100', '457', '0', '13824', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30945', '1', '0', 'NO! How is this possible?', '14', '0', '100', '457', '0', '13825', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30924', '0', '0', 'Now fight me, $N! Kill Iskalder!', '14', '0', '100', '0', '0', '1167', 'Possessed Iskalder');
INSERT INTO `creature_text` VALUES ('30945', '0', '0', 'Iskalder, there you are. What is this? Engaged in battle already?', '14', '0', '100', '457', '0', '13824', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('27480', '2', '0', 'Hold, lad. There\'s an inscription on the dais. It\'s a warning. It says, \"Whomsoever takes up this blade shall wield power eternal. Just as the blade rends flesh, so must power scar the spirit.\" Oh, I should\'ve known. The blade is cursed! Let\'s get the hell out of here!', '12', '0', '100', '0', '0', '12735', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '1', '0', 'You lied to your men and betrayed the mercenaries who fought for you. What\'s happening to you, Arthas? Is vengeance all that\'s important to you?', '12', '0', '100', '1', '0', '12734', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '0', '0', 'Isn\'t that a bit much, lad?', '12', '0', '100', '6', '0', '12733', 'Muradin');
INSERT INTO `creature_text` VALUES ('27455', '7', '0', 'Now, I call out to the spirits of this place. I will give anything or pay any price, if only you will help me save my people.', '12', '0', '100', '396', '0', '12732', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '6', '0', 'Damn the men! Nothing shall prevent me from having my revenge, old friend. Not even you.', '12', '0', '100', '5', '0', '12731', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '4', '0', 'Behold, Muradin, our salvation, Frostmourne.', '12', '0', '100', '1', '0', '12729', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '5', '0', 'I would gladly bear any curse to save my homeland.', '12', '0', '100', '1', '0', '12730', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '2', '0', 'Burned down to their frames! No one goes home until our job here is done!', '12', '0', '100', '15', '0', '12727', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '3', '0', 'Spare me, Muradin. You weren\'t there to see what Mal\'Ganis did to my homeland.', '12', '0', '100', '1', '0', '12728', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '1', '0', 'Uther had my troops recalled? Damn it! If my warriors abandon me, I\'ll never defeat Mal\'Ganis. The ships must be burned before the men reach the shore!', '12', '0', '100', '5', '0', '12726', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '0', '0', 'Captain, why are the guards not at their posts?', '12', '0', '100', '6', '0', '12725', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('21409', '4', '0', 'Their loss is most definitely my gain...', '12', '0', '100', '275', '0', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21410', '2', '0', 'As you wish, my lord.', '12', '0', '100', '66', '0', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('21409', '0', '0', 'Halt!', '12', '0', '100', '1', '0', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '1', '0', 'Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly!', '12', '0', '100', '5', '0', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '2', '0', 'Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed!', '12', '0', '100', '1', '0', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '3', '0', 'Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point.', '12', '0', '100', '1', '0', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21293', '0', '0', 'Hide! Hide before they see you! Back up!', '15', '0', '100', '0', '0', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21293', '1', '0', 'The time to strike is at hand. Terminate Icarius.', '15', '0', '100', '0', '0', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21293', '2', '0', 'You have it. Do not lose it again!', '12', '0', '100', '5', '0', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21410', '0', '0', 'What is it, my lord?', '12', '0', '100', '6', '0', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('21410', '1', '0', 'My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you.', '12', '0', '100', '1', '0', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('29919', '1', '0', 'By order of Duke Lankral, Commander of the Knights of the Ebon Blade, Vanquisher of the Scourge and Conquerer of The Shadow Vault, you are hearby served notice to cease and desist from all activities providing materiel assistance, vrykul, and any other type of support to the Lich King. Failure to comply will result in your immediate destruction. My messenger will serve as the instrument of my will. What is your decision?', '12', '0', '100', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('37554', '7', '0', 'BLASTED DEAD END! So this is how it ends. Prepare yourselves, heroes, for today we make our final stand!', '14', '0', '100', '5', '0', '17061', 'Sylvanas SAY_SYLVANA_TRAP');
INSERT INTO `creature_text` VALUES ('30409', '0', '0', 'You\'ve come for the doctor\'s plans! You\'ll only find death!', '12', '0', '0', '0', '0', '0', 'Apprentice Osterkilgr - Say 0');
INSERT INTO `creature_text` VALUES ('30409', '1', '0', 'The doctor entrusted me with the plans to Nergeld, our flesh giant amalgamation made entirely of vargul! It will be the most powerful creation of its kind and a whole legion of them will be created to destroy your pitiful forces!', '12', '0', '0', '0', '0', '0', 'Apprentice Osterkilgr - Say 1');
INSERT INTO `creature_text` VALUES ('33113', '0', '0', 'Hostile entities detected. Threat assessment protocol active. Primary target engaged. Time minus thirty seconds to re-evaluation.', '14', '0', '0', '0', '0', '15506', 'Flame Leviathan SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('33118', '0', '0', 'Insolent whelps! Your blood will temper the weapons used to reclaim this world!', '14', '0', '100', '0', '0', '15564', 'Ignis SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('33210', '1', '0', 'Move! Quickly! She won\'t remain grounded for long.', '14', '0', '100', '0', '0', '15648', 'Exp Commander SAY_GROUND_PHASE');
INSERT INTO `creature_text` VALUES ('33293', '1', '0', 'So tired. I will rest for just a moment!', '14', '0', '100', '0', '0', '15725', 'XT002 SAY_HEART_OPENED');
INSERT INTO `creature_text` VALUES ('33293', '0', '0', 'New toys? For me? I promise I won\'t break them this time!', '14', '0', '100', '0', '0', '15724', 'XT002 SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('32927', '4', '1', 'What have you gained from my defeat? You are no less doomed, mortals!', '14', '0', '100', '0', '0', '15663', 'Molgeim SAY_DEATH_2');
INSERT INTO `creature_text` VALUES ('32927', '4', '0', 'The legacy of storms shall not be undone.', '14', '0', '100', '0', '0', '15662', 'Molgeim SAY_DEATH_1');
INSERT INTO `creature_text` VALUES ('32930', '1', '1', 'YOU FAIL!', '14', '0', '100', '0', '0', '15588', 'Kologarn SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('32930', '1', '0', 'KOL-THARISH!', '14', '0', '100', '0', '0', '15587', 'Kologarn SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('32930', '0', '0', 'None shall pass!', '14', '0', '100', '0', '0', '15586', 'Kologarn SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('33515', '0', '0', 'Some things are better left alone!', '14', '0', '100', '0', '0', '15473', 'Auriaya SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('32915', '0', '0', 'Matron, the Conservatory has been breached!', '14', '0', '100', '0', '0', '15483', 'Elder Brightleaf SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('32906', '7', '0', 'The swarm of the elements shall overtake you!', '14', '0', '100', '0', '0', '15534', 'Freya SAY_SUMMON_LASHERS');
INSERT INTO `creature_text` VALUES ('32906', '6', '0', 'Children, assist me!', '14', '0', '100', '0', '0', '15533', 'Freya SAY_SUMMON_TRIO');
INSERT INTO `creature_text` VALUES ('32906', '5', '0', 'Eonar, your servant requires aid!', '14', '0', '100', '0', '0', '15528', 'Freya SAY_SUMMON_CONSERVATOR');
INSERT INTO `creature_text` VALUES ('32845', '1', '0', 'Tragic. To come so far, only to fail.', '14', '0', '100', '0', '0', '15553', 'Hodir SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('32845', '0', '0', 'You will suffer for this trespass!', '14', '0', '100', '0', '0', '15552', 'Hodir SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('33350', '1', '0', 'Now why would you go and do something like that? Didn\'t you see the sign that said \'DO NOT PUSH THIS BUTTON!\'? How will we finish testing with the self-destruct mechanism active?', '14', '0', '100', '0', '0', '15629', 'Mimiron SAY_HARDMODE_ON');
INSERT INTO `creature_text` VALUES ('33350', '0', '0', 'Oh, my! I wasn\'t expecting company! The workshop is such a mess! How embarrassing!', '14', '0', '100', '0', '0', '15611', 'Mimiron SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('32865', '1', '0', 'I remember you... In the mountains... But you... what is this? Where am--', '14', '0', '100', '0', '0', '15734', 'Thorim SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES ('32865', '0', '0', 'Interlopers! You mortals who dare to interfere with my sport will pay... Wait--you...', '14', '0', '100', '0', '0', '15733', 'Thorim SAY_AGGRO_1');
